css:
/*
Fragbite theme by lennyh
use width Stylish
*/

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("fragbite.se"),
               url-prefix(http://fragbite),
               url-prefix(http://www.fragbite)
{

body
{
background-color:#12202f !important;
background-image:url(http://i48.tinypic.com/e64yg3.png) !important;
background-repeat:repeat-x !important;
font-family:tahoma !important;
}

.main
{
padding:3px !important;
border:3px solid #12202f !important;
background-color:#29435e !important;
}

[style*="padding: 5px; background-color: black; float: right;"],
[style*="padding: 2px; float: right; background-color: black; font-size: 7pt;"],
[src*="http://annons.fragbite.com/www/images/b51a317d0d6ddd3b07e4da2afab8fa53.gif"]
{
display:none !important;
}

.spacer_top, .menu, .brief_subhead_left, .forum_thread_head, .forum_list_new, .quick_search_head, .comment_post_head, .comment_all_head, .brief_subhead_right, .news_front_head, .news_front_foot, .news_front_corner_down, .news_content_head, .news_content_list_head, .comment_all_head tbody, .heading, .subheading, .forum_head, .news_poll_head
{
background-image:url(http://i50.tinypic.com/eug7wg.png) !important;
height:30px !important;
font-weight:bold !important;
border:none !important;
}

.brief_subhead_left, .brief_subhead_right, .quick_search_head, .forum_list_new, .heading, .subheading
{
letter-spacing:0px !important;
text-indent:5px !important;
border-right:5px solid #12202f !important;
}

.heading, .subheading
{
padding:0px !important;
line-height:29px !important;
}

table.forum_list_new tbody
{
background-color:transparent !important;
font-weight:100 !important;
}

.brief_left, .brief_right
{
border-width:0px 3px 0px 3px !important;
border-color:#12202f !important;
padding:3px !important;
}

.forum_thread_head table tbody
{
background-color:transparent !important;
}

.menu_admin
{
background-color:#243a51 !important;
background-image:none !important;
height:20px !important;
}

.front_results_head
{
background-image:url(http://i50.tinypic.com/eug7wg.png) !important;
height:30px !important;
font-weight:bold !important;
padding:0px !important;
margin:0px !important;
}

.quick_search input
{
margin:5px !important;
}

.forum_front tbody tr td
{
background-color:#243a51 !important;
}

}
