css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("popmundo.com") {


td>img[src="images/trans_pixel.gif"] {
height:0px !important
}

img[src="graphics/Default/menu/GameHeader.jpg"],
img[src="graphics/Champagne/menu/GameHeader.jpg"] {
position:fixed !important; 
top:0px !important;
text-align:left !important;
float:left !important;
z-index:9999 !important;
}

tr.MainMenu {
border-bottom:7px solid #fff !important;
padding-left:10px !important;
position:fixed !important;
top:57px !important;
z-index:9999 !important;
}

tr.MainMenu>td {
width:580px !important;
}

tr.MainMenu>td+td {
width:135px !important;
padding-left:0px !important;
}

html>body>table.Paper>tbody>tr>td>table.Paper>tbody>tr>td {
text-align:left !important;
}

html>body>table.Paper>tbody>tr>td>table.Paper>tbody>tr>td.DarkColumnHL {
padding-left:7px !important;
}


html>body>table.Paper>tbody>tr>td>table.Paper>tbody>tr>td.Notifications {
text-align:center !important;
font-weight:bold; 
padding-top:7px !important;
padding-bottom:7px !important;
border-top:10px solid #fff;
}

html>body>table.Paper {
padding-top:5px !important;
}

}
