css:
@-moz-document domain("somtoday.nl") {
#header-wrapper {
    background-color: #000000;
}



body {
    line-height: 1;
    color: #000000;
    font-size: 12px;
    font-family: Open Sans,Arial,Helvetica,sans-serif;
    overflow-y: scroll;
    overflow-x: hidden;
    background: #000000;
        background-image: url("https://drive.google.com/uc?export=view&id=1H8tNcga6rl58CuPyHWw5OnOmqhVHYs0f");
 
    background-size: 100% ;
	 
}

#main-menu a {
    display: block;
    float: left;
    height: 30px;
    margin-right: 4px;
    font-size: 13.5px;
    border-radius: 5px 5px 0 0;
    padding: 12px 16px 4px;
    color: #ffffff;
    background: #000000;
}

h1 {
    font-weight: 700;
    font-size: 16pt;
    color: #000000;
}

.afspraak {
    position: absolute;
    left: 5px;
    right: 5px;
    background-color: #000000;
    color: #ffffff;
    margin: 2px;
    text-align: left;
}

.afspraakVakNaam {
    height: 14px;
    color: #ffffff;
    font-size: 13px;

}

#main-menu a.active, #main-menu a.active:hover {
    text-decoration: none;
    cursor: pointer;
    color: #fff;
    background: #000000;
}

#main-menu-bottom {
    height: 5px;
    background-color: #000000;
}

.today {
    border-bottom: solid 3px #000000;
    color: #fff000;
    font-weight: 700;
}
}
