css:
@-moz-document domain(www.roblox.com)
  {
@keyframes rainbow
  {
    0% {background-color: #60B5E6;
    border-color: #60B5E6;
  }

  14%
  {
    background-color: #70BBE6;
    border-color: #70BBE6;
  }

  28%
  {
    background-color: #80C1E6;
    border-color: #80C1E6;
  }

  42%
  {
    background-color: #90C7E6;
    border-color: #90C7E6;
  }

  56%
  {
    background-color: #A0CCE6;
    border-color: #A0CCE6;
  }

  80%
  {
    background-color: #90C7E6;
    border-color: #90C7E6;
  }

  94%
  {
    background-color: #80C1E6;
    border-color: #80C1E6;
  }

  100%
  {
    background-color: #70BBE6;
    border-color: #70BBE6;
  }
}

.btn-common-play-game-lg
{
  animation: rainbow 10s infinite;
}
  }
