css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url(http://www.drudge.com/){
BODY,P{background:white !important;}
*{font-size:18px !important;}
a{text-decoration:none !important;}
a:visited{color:gray !important;}
a:hover
{text-decoration:underline !important;
color:black !important;
background-color:white !important;}
iframe,ins,.loginbox,BODY>BR,BODY>DIV:first-child+P,
BODY>DIV:first-child+P+P,BODY>DIV:first-child+P+P+P,
BODY>DIV:first-child+P+P+P+DIV,TD[width="15"],
TD[width="150"],TD[width="150"]+TD,.rsstop,
img[src$="logosmall.gif"],BODY>P:first-child,
BODY>SCRIPT:first-child+P,TD:not(.newscolumn),
#change_FlashContainer,#change_AdContainer,.adlabel
{display:none !important;}}

@-moz-document url-prefix(http://www.drudge.com/news/){
BODY{background-color:white !important;}
.entry{padding-bottom:0 !important;}
.entryfooter
{border-bottom-width:1px !important;
 border-bottom-color:black !important;
 border-bottom-style:solid !important;
 padding-bottom:5px !important;}
#title,TD[width="122"],TD[width="102"],iframe,.rsstop,
.commenthelp,SELECT,.dateheader+FORM,ins
{display:none !important;}}
