/* ==UserStyle==
@name           Dark Republic for Twitter
@namespace      github.com/officialcreyo/dark-twitter
@version        1.0.0
@description    Dark Style for Twitter web application.
@author         Creyo
==/UserStyle== */
@-moz-document domain("twitter.com") {

    :root {
        --dt-darker: #181a1e;
        --dt-darke: #1b1f26;
        --dt-dark: #1f232a;
        --dt-dark-l: #252a33;
	    --dt-dark-ml: #333943;
    }
    
    element.style {
        background-color: var(--dt-dark) !important;
    }
    
    body {
        background-color: var(--dt-dark) !important;
        background-attachment: fixed;
        background-position: center 46px !important;
        background-repeat: no-repeat;
    }

    /*
 * --------------------------------------------------------------------------------------------------------------------
 */
    /*
 *--------------------------Font--------------------
 */
    .r-1b6yd1w {
        font-size: 16px;
    }

    /*
 *--------------------------Twitter Logo--------------------
 */
    .r-mtrfb5 {
        min-width: 40px;
    }

    .r-1vvnge1 {
        margin-left: 120px;
    }

    .r-1jayybb {
        min-height: 40px;
    }

    /*
 *--------------------------TL Content--------------------
 */
    .r-a023e6 {
        font-size: 14px;
    }

    .r-1uaug3w {
        background-color: var(--dt-dark);
    }

    .r-ii8lfi {
        background-color: rgb(31 35 42 / 0.1);
    }

    /*
 *--------------------------Right Sidebar--------------------
 */
    .r-1uhd6vh {
        background-color: var(--dt-dark);
        max-width: 350px;
        padding-right: 10px;
        border-bottom-color: var(--dt-dark-ml);
        border-left-color: var(--dt-dark-ml);
        border-right-color: var(--dt-dark-ml);
        border-top-color: var(--dt-dark-ml);
    }

    .r-yfoy6g {
        background-color: var(--dt-dark);
    }
    
    .r-g6ijar {
        background-color: var(--dt-darke);
    }
    
    .r-74htps {
        border-color: var(--dt-darke);
    }

    .r-1joea0r {
        margin-left: 2px;
    }

    .r-1ovo9ad {
        width: 350px;
        margin-right: 50px;
        ;
    }

    .r-1l5qxre {
        padding-bottom: 0px;
        background-color: rgb(31, 35, 42, 0.7);
        padding-left: 5px;
        padding-right: 5px;
        border-bottom-left-radius: 14px;
        border-bottom-right-radius: 14px;
        border-top-left-radius: 14px;
        border-top-right-radius: 14px;
    }

    /*
 *--------------------------Top bar--------------------
 */
    .r-1h3ijdo {
        height: 45px;
    }

    .r-aaos50 {
        background-color: var(--dt-dark-ml);
    }

    .r-xnswec {
        box-shadow: rgb(31 35 42 / 0.75) 0px 0px 15px, rgb(143 149 155 / 0.15) 0px 0px 3px 1px;
    }

    /*
 *--------------------------Left sidebar--------------------
 */
    .r-d0pm55 {
        max-width: 500px;
        padding-left: 20px;
        margin-left: 16px;
        background-color: rgb(31, 35, 42, 0.7);
        border-bottom-left-radius: 14px;
        border-bottom-right-radius: 14px;
        border-top-left-radius: 14px;
        border-top-right-radius: 14px;
    }

    .r-e7q0ms {
        max-width: 250px;
        padding-left: 20px;
        margin-left: 16px;
    }

    /*
 *--------------------------Scrollbar--------------------
 */
    ::-webkit-scrollbar {
	    width: 10px;
    }

    ::-webkit-scrollbar-track {
        background: #1f232a;
    }

    ::-webkit-scrollbar-thumb {
        background: #333943;
    }

    ::-webkit-scrollbar-thumb:hover {
        background: #272d35;
    }

    /*
 *--------------------------Who to follow--------------------
 */
    .r-gzqks2 {
        min-height: 20px;
        max-height: 300px;
    }

    /*
 *--------------------------Twiiter info & "X retwitted"--------------------
 */
    .r-1sf4r6n {
        width: auto;
        max-width: 400px;
    }

    .r-13wfysu,
    .r-1ddef8g {

        max-width: 400px;
    }

    .r-ry3cjt {
        width: auto;
        max-width: 200px !important;
    }
}
