css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(zive.sk), domain(mobilmania.sk) {
td.zivelogo, tr.pozadi-head > td > a > img,
.top-250x250banner,
.reklama-250x250a,
div.etarget,
div.spodni-lista,
iframe#etargetiframe,
div.reklama-dalsi,
div.reklama-250x250,
div.reklama-top {
           display: none !important;
}
td.blesk,
td.headline-box {
           background-color: #FDF5E6 !important;
}
td.blesk {
           padding: 0 !important;
}
div.bleskovky-pozice {
           padding: 0 0 0 5px !important;
           font-size: 1.1em !important;
}
h2.box,
h2 span a,
td.kapitoly-seznam-tit, tr.kapitoly-navigace td,
tr.hlavicka1 td,
tr.diskuze-popis td,
td.blesk > h2, td.blesk > h2 a,
td.headline-box > h2, td.headline-box > h2 a,
td.levy-sloupec h2, td.levy-sloupec h2 a,
td.pravy-sloupec h2, td.pravy-sloupec h2 a
            { background-color: #E79F69 !important;
              color: Black !important;
}

div.prehled-clanku > h2,
div.prehled-clanku > h2 a { color: Black !important;
                            background-color: #FFFFFF!important;
}

.pozadi-head { background-color: Black !important; 
               background-image: none !important;
}
tr.hlavicka2,
table.menu tbody tr td a { background-color: #8B4513 !important; }
tr.hlavicka2 { color: #FFFFFF !important; }
/*
body > div {display: none !important;}
*/
.pozadi-tabulka,
table.prispevky-vypis .hlavicka,
table.prispevky-vypis .hlavicka-0,
.prispevky,
.kapitoly-seznam,
.blackbar a, .blackbar a:hover, .blackbar a:visited,
.chlivky,
#navbar div.ramecek,
.hlaska div, .chyba div
              {background-color: #FAFAD2 !important;}

iframe {display: none !important;}

body p {font-family: Tahoma !important;}
}

@-moz-document domain(mobilmania.sk) {
table.menu tbody tr td a { background-color: #375E3C !important; }
a.login-box-reg { background-color: #DAA520 !important; }
}
