css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("vkontakte.ru") {

h1#home {
 visibility: hidden;
}
#pageLayout, #pageHeader, #history #historyContainer div, div#opinions form#postMessage textarea, table.profileTable tbody tr td.data div.dataWrap, #pageBody #wrap2 #dialog #messageFields, #pageBody #wrap2 #dialog #messageFields table, div.playerClass, div#wall table.wallpost div.text, #pageBody #wrap2 #content #dialog, table.feedTable td.feedStory, #msgs .mailbox > table, #msgs .mailbox td.messageSnippet, #msgs .mailbox td.messageSnippet div [style], #messageHistory table[style], #messageHistory td.message div[style], #fave .people_table, #audioBar .mainPanel {
 width: 100% !important;
}
#topNav {
 position: absolute;
 left: 700px;
}
#pageHeader, #topNav a, #topNav div, #pageBody #wrapHI #header {
 background: transparent !important;
}
#topNav a {
 color: #A0A0A0 !important;
}
#topNav a:hover {
 color: black !important;
 text-decoration: none !important;
}
#pageHeader {
 height: 40px !important;
}
#pageBody {
 width: 88% !important;
}
#userProfile #rightColumn {
 width: 80% !important;
}
table.profileTable tbody tr td.label {
 width: 150px !important;
}
div.flexBox div.edit {
 float: right !important;
}
div#audios div.playline {
 width: 63% !important;
}
div.playerClass, div#wall table.wallpost div.text {
 text-align: center
}
div#wall table.wallpost div.text div.audioRowWall div[style="float: left;"] {
 width: 775px !important;
}
/*div#wall table.wallpost div.text div.audioRowWall div.duration {
 position: relative !important;
 top: -17px;
}
div#wall table.wallpost div.text div.audioRowWall table tbody td div {
 height: 10px !important;
}*/
div#wall table.wallpost td.info, div#wall table.wallpost td.info div.header, #dialog td.dialog, #dialog, #pageBody div, #dialog td.dialog h4 {
 border: none !important;
}
#messageFields tr[style] td.label, #shadowLine, #msgs .mailbox td.messagePicture, #msgs .mailbox td.messageActions div br, .mailbox th.messagePicture, .mailbox th.messageFrom, .mailbox th.messageSnippet, .mailbox th.messageActions, div#checkboxFeed, #postMessage td.label {
 display: none;
}
#messageFields tr.tallRow td[style] div[style], #messageFields tr.tallRow td[style], #messageHistory, #postMessage #title {
 width: 1000px !important;
}
#content {
 padding: 0 !important;
}
#dialog {
 margin: 0 !important;
}
#dialog #postMessage #reply_field {
 width: 1080px !important;
 height: 200px !important;
}
#msgs .mailbox td.messageActions {
 float: right !important;
 border: none !important;
}
.mailbox th.messageChooser, .mailbox th.messageStatus, .mailbox td.messageChooser, .mailbox td.messageFrom, .mailbox td.messageStatus, #audioBar {
 background: transparent !important;
 border: none !important;
}
.mailbox td.messageFrom {
 float: left !important;
 position: relative;
 top: -8px;
}
#fave #leftColumn {
 width: 900px !important;
}
#postMessage .formTable td {
 width: 10px !important;
}
#postMessage #messageBody {
 width: 1000px !important;
 height: 200px !important;
}
#audioBar #audios .audioRow table td[style="width: 20px; vertical-align: top;"] {
 width: 2% !important;
}
#editor .row textarea {
 width: 760px !important;
}
#editor .row div.labeled textarea {
 line-height: 1.5 !important;
 font-size: 12pt !important;
}
#editor .details {
 float: right !important;
 margin-right: 10px;
}
#searchResults td[style] .info {
 width: 700px !important;
}
#searchResults td[style] .info dd {
 width: 600px !important;
}
#searchResults .grouprow {
 width: 100% !important;
}
#searchResults .grouprow .info {
 width: 840px !important;
}
#searchResults .grouprow .info .printcontent, #searchResults .grouprow .info .printcontent div {
 width: 100% !important;
}
#startagroup {
 width: 40% !important;
}
#findagroup {
 width: 50% !important;
}
#findagroup .inputText {
 width: 400px !important;
 margin-right: 20px !important;
}
.groupsLookupCont input#g_filter {
 width: 300px !important;
}
#pageHeader #quickSearch #qquery{
 width: 170px !important;
}
#notesBar #mainPanel {
 width: 950px !important;
}
#notesBar #mainPanel .note_body {
 width: 865px !important;
}
.simpleBlock .simpleBar {
 text-align: center !important;
 padding-bottom: 10px !important;
}
.simpleBlock .simpleBar .buttonRow {
 margin-left: 420px !important;
}
#searchResults .column {
 width: 1105px !important;
}
#content div.match {
 width: 230px !important;
}
#content div.match td.matchInfo {
 background: transparent !important;
 border: none !important;
}
#content div.matchSent {
 width: 1040px !important;
}
#content div.matchSent td.matchSentInfo {
 width: 100% !important;
}
#content #oneMatch {
 margin-left: 200px !important;
 width: 500px !important;
}
#content #oneMatch td.oneMatch, .results .info dd {
 width: 100% !important;
}
table#dialog td.dialog div#messageFields td.label {
 display: none;
}
table#dialog td.dialog div#messageFields td.messageText * {
 width: 100% !important;
}
table#dialog td.dialog div#messageFields tr[style="height: 19px;"] div[style] {
 width: 100% !important;
}
div#messageHistory tr.message_shown td.incoming, div#messageHistory tr.message_shown td.outgoing {
 display: none;
}
div#messageHistory tr.message_shown td.outgoing + td.message_history {
 color: #000000 !important;
 width: 100% !important;
}
div#messageHistory tr.message_shown td.incoming + td.message_history {
 color: #700000 !important;
 width: 100% !important;
}
div#messageHistory tr.message_shown td.message_history div {
 width: 100% !important;
}
div#messageHistory tr.message_shown td.outgoing + td.message_history div a {
 color: black !important;
 width: 100% !important;
 font-style: italic;
}
div#messageHistory tr.message_shown td.incoming + td.message_history div a {
 color: #700000 !important;
 width: 100% !important;
 font-style: italic;
}
input#edit_activity_text {
 width: 630px !important;
}
div#edit_activity_container {
 border: 1px solid black !important;
}

}

@-moz-document url-prefix("http://vkontakte.ru/gsearch.php?q=") {

#qquery {
 position: absolute !important;
 left: 145px;
 width: 1000px;
 border: none !important;
 top: 9px;
}
#qinput {
 width: 1034px !important;
 border: 1px solid #A0A0A0 !important;
 font-family: Courier !important;
 font-size: 14px !important;
 padding: 1px 0 1px 5px !important;
 height: 18px !important;
}
#qchoose div {
 position: absolute !important;
 left: 890px;
 top: 1px;
}
#qdropdown {
 left: 1033px !important;
 top: 32px !important;
}
#results > div {
 width: 100% !important;
}
.audioText {
 width: 95% !important;
}

}

@-moz-document url-prefix("http://vkontakte.ru/blank.php") {

.nNav {
 display: none;
}
div.simpleBar {
 padding-top: 10px !important;
}

}

@-moz-document url-prefix("http://vkontakte.ru/wall.php") {

div#wall table.wallpost div.text div.audioRowWall div[style="float: left;"] {
 width: 970px !important;
}

}
