css:
/* I got tired of looking at the ugly forum colors.  Feel free to modify */
/* More can be done for sure.                                            */

/* by: kwliving@gmail.com */
/*                        */
/* 2009-12-22
   - added input, textarea
   - buttons glow, input area is purple, yellow text
   - darkened colors
   2009-12-25 
   - "raised" the textsmall so it's easier to read.
   2010-01-26
   - made small links inline boxes so they're easier to click
   - added color to small links
*/

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forums.plentyoffish.com"), url("http://pics.plentyoffish.com")
{
.normalTextSmall, .normalTextSmallBold, .normalTextSmaller {
     font-family: Tahoma,Verdana,Arial,Helvetica,sans-serif !important;
     text-shadow:1px 1px 1px #000000 !important;
}
body { 
     background-color: #251132 !important; 
     color: #ffffff !important;}

a {color: #ffe17f !important; }
a:link { color: #ffe17f !important; }
a:visited { color: #ffe17f !important; }
a:active { color: #ffe17f !important; }
a:hover{ color: #ffe17f !important;
    background-color: #202 !important; }

td {
     font-family: Tahoma, Verdana, Arial, sans-serif !important;
     color: white !important; }

.TableHeaderText { 
     color: black !important; 
     background-color: #fff0bf !important; 
     font-size: 14px;}

.forumHeaderBackgroundAlternate { background-color: #251132 !important; }
.forumAlternate {background-color:#373059 !important; }
.forumRow {background-color:#484459 !important; }
.forumRowHighlight {
     background-color:#1d173b !important; 
     color: #ffffff !important;}

.normalTextSmall, .normalTextSmallBold, .normalTextSmaller {
     color:#ffffff !important;}

.quote {
     background-color: #251132 !important;
     border:2px solid gray;
     padding:3px;
     color: #fff0bf;
     font-style: italic;
     margin-left: 30px;
     line-height: 1 !important;}

.quote::before {
     font-family: serif;
     font-size: 24px;
     content: "\201c \00a0";
}
.quote::after {
     font-family: serif;
     font-size: 24px;
     content: "\00a0 \201d";}

/* Eliminate banner */
     body img[src$="forumhead.jpg"] { display: none; }

select {
     background-color: #4a2265 !important;
     color: yellow !important;
     font-size: 13px !important;
}
input { 
     -moz-appearance: none !important;
     background: transparent !important;
     background-color: #4a2265 !important;
     border: yellow 2px solid;
     color:yellow ;
}
input[type="submit"],input[type="input"] { 
     color: yellow !important;
     text-shadow:1px 1px 1px #ffe17f !important;
     padding: 2px 3px 4px !important;
     -moz-box-shadow:0 0 1em 0 #FFFFFF; 
}
textarea {
     -moz-appearance: none !important;
     background-color: #373059 !important;
     color: #ffe17f !important;
}
a.linkSmall {
     padding-left: 4px;
     padding-right: 4px;
     padding-bottom: 1px;
     display: inline-block;
     text-align: center;
     border: 1px solid #444;
     color: #cc3 !important;
     background-color: #114;
     text-decoration: none;
}
body table tr td {
	background-image: none !important;
	color: #FFC !important;
}
body table tr td a {
	color: #909 !important;
        text-decoration: none  !important;
}
body table tr td a font {
	color: #f6f !important;
}


}
