css:
@-moz-document url-prefix("https://tracker.yandex.ru"), url-prefix("http://tracker.yandex.ru") {
:root {
/* 	
    переменная photo-bg-url хранит ссылку на фото для background
*/
 --photo-bg-url: url("https://img5.goodfon.ru/original/1920x1080/3/6e/milky-way-summt-lake-ozero-molochnyi-put-zviozdy-gory-gora-n.jpg?d=1");
 --agile-bg-main: var(--photo-bg-url) no-repeat 0px -100px;
}

.b-page-dashboard__content {
	background:var(--photo-bg-url);
}

.b-agile-board__fixed-header {
	background:#fff
}
.b-agile-board__header {
	background:#fff
}

.b-page-agile-board__qfilters-box {
	background:#fff
}

.b-agile-board__main {
	background:rgb(0 0 0 / 20%);
}
}
