css:
@-moz-document url-prefix("https://leakedreality.com/video") {
* {
    max-width: unset !important;
}

@media (min-width: 768px) {
    .col-md-7 {
        flex-basis: 100%;
        max-width: 100%;
    }
}

.navigation,
.support-progress,
.main-container > .text-center,
.site-footer,
.widget_viewtube_related_videos,
.video-shere {
    display: none !important;
}

.main-container {
    padding-left: 0;
    padding-right: 0;
}

.pb-100 {
    padding-bottom: 10px;
}

.comments-area {
    margin-top: 10px;
    padding: 10px 10px;
}

.entry-content {
    padding: 10px;
}

.container {
    margin-left: 10px;
    margin-right: 10px;
}

.comment-form textarea {
	height: 100px;
}

video.detail_page_video {
	height: unset;
}

.site-header {
    padding: 5px 0;
}

.sidebar-menu .desktop-menu {
    height: 100vh;
    padding-bottom: 40px;
}

.topspace-90 {
    margin-top: 50px;
}

.sidebar-menu .desktop-menu {
    height: 99vh;
}

video.detail_page_video {
    max-height: calc(100vh - 135px);
}
}
