css:
@-moz-document domain(portal.offensive-security.com) {
	:root {
	--main-bg-colour: #282828;
	--main-bg-header-colour: #212121;
	--main-accent-colour: #9900cc;
	--main-accent-colour-darker: #880099;
	--main-bg-light: #EEEEEE;
	/*--main-offsec-red-accent-colour: rgb(169, 12, 28);*/
	/*--primary: #610094;*/
    }

    body {
        background-color: var(--main-bg-colour);
        color: white;
    }

    .module-name {
        color: white;
    }

    table tbody tr td {
        color: white;
    }

    .text-black, a.text-black:focus, a.text-black:hover, .subheader-row{
        color: white !important;
    }

    div.subheader-container.full-width.pr-5.pl-5 {
        background-color: var(--main-bg-header-colour);
    }

    .header[data-v-65b6265c]{
        background-color: var(--main-accent-colour);
    }

    .font-weight-bold {
        color: var(--main-accent-colour);
    }

    div#book{
        border-bottom: 3px solid var(--main-accent-colour);
    }

    .module-row:hover .module-name {
        color: var(--main-accent-colour);
    }

    .menu-link.active[data-v-a5bc7b26] {
        color: var(--main-accent-colour);
    }

    .btn-primary {
        background-color: var(--main-accent-colour);
        border-color: var(--main-accent-colour-darker);
    }

    .btn-primary:not(:disabled):not(.disabled).active, .btn-primary:not(:disabled):not(.disabled):active, .show>.btn-primary.dropdown-toggle {
        background-color: var(--main-accent-colour);
        border-color: var(--main-accent-colour-darker);
    }

    .action-icon {
        color: var(--main-accent-colour-darker);
    }

    .progress .progress-bar.bg-secondary{
        background-color: var(--main-accent-colour) !important;
    }

    .form-control {
        background-color: var(--main-bg-colour);
    }

    .activity-card .activity-header[data-v-63515ace]

    .activity-header > span {
        color: var(--main-bg-light);
    }

    .activity-card {
        border: 1px solid var(--main-bg-header-colour) !important;
    }

    .selected.underlined {
        border-bottom: 3px solid var(--main-accent-colour) !important;
    }

    div#RULES, div#LEADERBOARD, div#MACHINE_LIST{
        color: white;
    }

    div#RULES.selected, div#LEADERBOARD.selected, div#MACHINE_LIST.selected{
        color: var(--main-accent-colour);
    }

    span.cursor-pointer.subscribe {
        color: var(--main-accent-colour);
    }

    a, a:hover {
        color: var(--main-accent-colour);
    }

    .cart-title {
        color: white !important;
    }

    div.cart-row > div > p {
        color: black !important;
    }

    button.buy-more-btn {
        color: var(--main-accent-colour) !important;
    }

    .videos-navigation button.btn[data-v-10fbc1ef] {
        color: var(--main-accent-colour);
    }

    li.section-title.active > span.flex-grow {
        color: var(--main-accent-colour);
    }

    /*div.module-sidebar-container {
        background-color: var(--main-bg-colour);
    }*/

    .module-sidebar {
        background-color: var(--main-bg-colour);
        border: 1px solid var(--main-bg-header-colour);
    }

    span.display-list-item.selected {
        color: var(--main-accent-colour) !important;
    }

    div.title-bar.d-flex.flex-row {
        background-color: var(--main-bg-header-colour);
    }

    div.course-content-container {
        background-color: var(--main-bg-colour);
    }

    div.right-sidebar {
        background-color: var(--main-bg-colour);
        border: 1px solid var(--main-bg-header-colour);
    }

    .sections-tree>li.selected span, .sections-tree li.selected {
        color: var(--main-accent-colour);
    }

    .markdown-content .sb-block {
        color: var(--main-accent-colour);
        border-top: 1px solid var(--main-accent-colour);
        border-bottom: 1px solid var(--main-accent-colour);
    }

    .markdown-content section.sb-block p {
        color: var(--main-accent-colour);
    }

    .markdown-content h1, .markdown-content h2, .markdown-content h3, .markdown-content h4, .markdown-content h5, div.markdown-content > div > p, .markdown-content p  {
        color: white;
    }

    .markdown-content .footnote-container {
        border-top: 1px solid white;
    }

    div.footnote-item > p{
        color: white;
    }

    .tab-item.selected.filled[data-v-08744803] {
        background-color: var(--main-accent-colour);
    }

    .tab-item.selected.filled[data-v-08744803] > span {
        background-color: var(--main-accent-colour);
        color: white;
    }

    .notes-type[data-v-bf053720] {
        color: var(--main-accent-colour);
    }

    .module-modal .title[data-v-bf053720]{
        color: white;
    }

    button.btn[data-v-01419b73], button.btn svg[data-v-01419b73]{
        color: var(--main-accent-colour);
        stroke: var(--main-accent-colour);
    }

    .module-modal .title{
        color: white;
    }

    .module-sidebar, .right-sidebar, .title-bar{
        border: 1px solid var(--main-bg-header-colour);
    }

    .module-modal .action-icon-close {
        color: var(--main-accent-colour);
    }

    .active .plus-minus-sign, .expanded.active .plus-minus-sign {
        color: var(--main-accent-colour);
    }


    .sections-tree li.active .counter-holder .display-list-item {
        color: var(--main-accent-colour);
    }

    .markdown-content .code-block-p, .markdown-content .code-block-ui{
        color: var(--main-accent-colour);
        background-color: var(--main-bg-header-colour);
    }

    .markdown-content pre.fence-code {
        background-color: var(--main-bg-header-colour);
        color: white;
    }
}
