css:
@-moz-document domain("ffg.jeudego.org") {
body{
    background-color: #282830;
    color: #DDD;
}

#ffg_main, #ffg_page, #ffg_footer {
    color: #f4dba4;
    background-color: #291E1E;
}
#ffg_header_searches{
    background-color: #140000;
}
#ffg_searches{
    background-color: #2e231f;
}
.news_header {
    background-color: #333333;
}
#ffg_news_pubs > * {
    border-color: #634200;
}
#ffg_header_imgs{
    background-image: none;
}
a,
a:visited {
    color: #f4dba4;
}
a:hover {
    color: #efa276;
}
#ffg_main_content {
    background-color: #2f221d;
    border-color: #333333;
}

td {
    color: #f7e7bb;
}
table.simple td{
    border-color: #423d36;
    background-color: #151515;
}
table.simple td.pair{
    background-color: #252020;
}
table.simple td.impair{
    background-color: #2b1504;
}
th {
    background-color: #6f4123;
}
table.simple td.premier {
    background-color: #453126;
}
input[type=text]{
    background-color: #404040;
    color: #DDD
}
div.authMessage {
    background-color: #3a3735
}
.bloc_entete {
    border: 1px double #B45700;
    background-color: #000000;
}

#ffg_main_menu .menu_FFG_item:nth-child(14) a{
    font-size: 2em;
    font-weight: bolder;
    color: #ead4be;
}
#ffg_main_menu .menu_FFG_item:nth-child(16) a{
    font-size: 1.4em;
    color: #fd7c35;
}
.leaflet-popup-content-wrapper, .leaflet-popup-tip {
    background: #453126;
}

}
