css:
@-moz-document domain("dribbble.com") {
  /*****  general  *****/
  body {
    color: #ffffff;
    background-color: #2f2f2f;
  }

  /*****  header  *****/

  input[type="text"],
  input[type="number"],
  input[type="search"],
  input[type="password"],
  input[type="email"],
  input[type="url"],
  input[type="tel"],
  .braintree-input,
  .selectize-input,
  textarea,
  .ql-container .ql-editor,
  select,
  .input-with-prefix {
    background-color: #2f2f2f;
    color: #0d0c22;
  }

  .site-nav,
  .site-nav-desktop-only path {
    fill: #ea4c89;
  }

  .site-nav-avatar {
    border: #ea4c89 2px solid;
  }

  .site-nav {
    background: #232323;
    border-bottom: #ea4c89 solid 2px;
  }

  /*link header*/
  .site-nav-desktop-nav .site-nav-desktop-item {
    color: #b6b0fb;
  }

  .site-nav-desktop-nav .site-nav-desktop-item:hover a {
    color: #5c52cb;
  }

  .site-nav .site-nav-container {
    padding-left: 24px;
    padding-right: 24px;
    margin-left: 0;
    margin-right: 0;
}

  /*  tag nav  */
  .form-sub.outlined:hover,
  .form-sub.outlined:focus,
  .form-btn.outlined:hover,
  .form-btn.outlined:focus,
  .form-blurple.outlined:hover,
  .form-blurple.outlined:focus,
  .form-forest.outlined:hover,
  .form-forest.outlined:focus,
  .form-gradient.outlined:hover,
  .form-gradient.outlined:focus,
  a.form-sub.outlined:hover,
  a.form-sub.outlined:focus,
  a.form-btn.outlined:hover,
  a.form-btn.outlined:focus,
  a.form-blurple.outlined:hover,
  a.form-blurple.outlined:focus,
  a.form-forest.outlined:hover,
  a.form-forest.outlined:focus,
  a.form-gradient.outlined:hover,
  a.form-gradient.outlined:focus {
    color: #ea4c89;
  }

  .form-sub.outlined,
  .form-btn.outlined,
  .form-blurple.outlined,
  .form-forest.outlined,
  .form-gradient.outlined,
  a.form-sub.outlined,
  a.form-btn.outlined,
  a.form-blurple.outlined,
  a.form-forest.outlined,
  a.form-gradient.outlined {
    color: #ffffff;
  }

  .filter-categories .scroll a {
    color: #ea4c89;
  }

  .filter-subnav {
    background: #232323;
  }

  .filter-categories .category.active a,
  .filter-categories .category a:hover {
    color: #ea4c89;
  }

  .filter-categories .scroll-forward a {
    background-image: linear-gradient(
      to right,
      rgba(255, 255, 255, 0) 0%,
      #232323 50%
    );
  }

  /*****  behind grid  *****/
  #wrap {
    background: #232323;
  }

  .shot-display-template
    .shot-details-container
    .user-information
    .display-name,
  .shot-thumbnail-container
    .shot-details-container
    .user-information
    .display-name {
    color: white;
  }

  .shot-container .shot-content {
    background-color: #232323;
  }

  .shot-container .shot-content {
    background-color: #232323;
  }

  .shot-container .shot-description-container {
    color: white;
  }

  .shot-container .shot-description-container {
    color: white;
  }

  .site-nav .site-nav-container {
    background-color: #232323;
  }

  /***** Grid *****/
  .color-deep-blue-sea-light-20 {
    color: #9e9ea7;
  }

  h1 span.badge,
  span.badge {
    color: #3d3d3d;
  }

  /***** footer  *****/
  .site-footer {
    color: #b6b0fb;
  }

  .site-footer .social-links-container a {
    color: #b6b0fb;
  }

  .site-footer .link-heading {
    color: #ea4c89;
  }

  .site-footer .link {
    color: #b6b0fb;
  }

  .link{
   color:#b6b0fb;
  }

  .site-footer .link {
    color: #b6b0fb;
  }

  .site-footer {
    background: #232323;
    border-top: #ea4c89 solid 2px;
  }

  .site-footer .total-shots-container .shots-count {
    color: #ea4c89;
  }

  /***************|ZOOM Modal|**************/
  .user-details-section .user-bio {
    color: rgb(255 255 255);
  }
  .user-details-section .user-name {
    color: #b6b0fb;
  }
  .shot-container .shot-header-action-links .follow-link a {
    color: rgb(147 147 147);
  }

  .shot-container .shot-user-link {
    color: #b6b0fb;
  }

  .shots-related-goods-container {
    background-color: rgb(35 35 35);
  }

  .injected-goods {
    background-color: rgb(35 35 35);
  }

  .good-title-text:link,
  .good-title-text:visited {
    color: #ea4c89;
  }
}
