/* ==UserStyle==
@name         Animego Wide Player
@version      20220406.09.58
@namespace    userstyles.world/user/vulperion
@description  Широкий плеер для AnimeGo.org и фикс скроллбара
@author       vulperion
@license      No License
==/UserStyle== */

@-moz-document domain("animego.org") {
div.video-player-bg {
    flex-direction: column;
    align-items: center;
}

div.video-player-main {
    max-width: 100%;
}

div.video-player-border-left {
    max-width: 50%;
}

div.video-player-side {
    text-align: center;
}

div.video-player-toggle{
    overflow: auto;
    height: 150px;
}

div.px-4{
    padding-left: 1rem!important;
    padding-right: 1rem!important;
}

::-webkit-scrollbar{
    width: 5px;
    height: 10px;
    background-color: #8c8c8c;
    border-radius: 1em;
}
::-webkit-scrollbar-thumb{
    background-color: #434343;
    border-radius: 1em;
}
}
