css:
@-moz-document domain("austiblox.net") {
.ow_site_panel {
    background-color: #282828;
}

html {
    color: #ffffff;
}

.ow_bg_color {
    background-color: #282828;
}

.ow_main_menu li a {
    color: #fff;
}

.ow_page_container {
    background: #1e1e1e;
}

h1,
h2,
h3,
h4,
h5 {
    color: #999;
}

h1 {
    color: #999;
}

html,
body,
div,
span,
applet,
object,
iframe,
h1,
h2,
h3,
h4,
h5,
h6,
p,
blockquote,
pre,
a,
abbr,
acronym,
address,
big,
cite,
code,
del,
dfn,
em,
font,
img,
ins,
kbd,
q,
s,
samp,
small,
strike,
strong,
sub,
sup,
tt,
var,
b,
u,
i,
center,
dl,
dt,
dd,
ol,
ul,
li,
fieldset,
form,
label,
legend,
table,
caption,
tbody,
tfoot,
thead,
tr,
th,
td {
    background: #282828;
}

.ow_avatar_console {
    background: #282828;
    border: 1px solid #3c3c3c;
}

.ow_box_empty {
    background-color: #282828;
}

.ow_add_content {
    background-color: #3c3c3c;
    color: #b3b3b3;
}

.ow_alt1,
tr.ow_alt1 td {
    background-color: #282828;
}

.ow_table_1 tr.ow_tr_first th,
.ow_table_2 tr.ow_tr_first th {
    border-color: #3c3c3c;
}

.ow_table_1 tr td,
.ow_table_2 tr td,
.ow_user_list .ow_alt1,
.ow_user_list .ow_alt2,
.ow_video_list .ow_alt1,
.ow_video_list .ow_alt2,
.ow_photo_list .ow_alt1,
.ow_photo_list .ow_alt2 {
    border-color: #3c3c3c;
}

.ow_box_cap_empty {
    background-color: #282828;
    margin-bottom: 8px;
}

input[type=text],
input[type=password],
textarea,
select,
div.jhtmlarea,
.ow_photo_upload_description,
.ow_photo_preview_edit .CodeMirror {
    background-color: #282828;
    border: 1px solid #3c3c3c;
    color: #999;
}

.ow_newsfeed .ow_newsfeed_body {
    border: 1px solid #3c3c3c;
}

th,
.ow_highbox_table .ow_highbox,
.ow_page .ow_highbox,
.ow_sidebar,
.ow_table_3 td {
    border-color: #3c3c3c;
}

.ow_highbox_table .ow_highbox,
.ow_highbox {
    background: #282828;
    box-shadow: 0px 0px 1px #3c3c3c;
}

body form div.jhtmlarea {
    background: #282828;
}

.ow_main_menu li a {
    color: #999;
}

.ow_box {
    background-color: #282828;
    border: 1px solid #3c3c3c;
}

.ow_box_cap {
    background: #282828;
    border-top: 1px solid #3c3c3c;
    border-left: 1px solid #3c3c3c;
    border-right: 1px solid #3c3c3c;
}

.ow_feed_comments_viewall,
.ow_photo_attachment_preview,
.ow_oembed_attachment_preview,
.ow_oembed_attachment_pic {
    background-color: #282828;
    border-color: #3c3c3c;
}

.ow_footer {
    background: #3c3c3c;
}

a.avatar_list_more_icon {
    background: #282828;
}

body .ow_chat_dialog .ow_author_block {
    border-color: #3c3c3c;
}

.ow_content_menu {
    border-bottom: 1px solid #282828;
}

.game {
    border: 3px rgb(169 0 0) solid;
}

.ow_alt2,
tr.ow_alt2 td {
    background-color: #282828;
}

.ow_tooltip .ow_tooltip_body {
    background: #000;
    padding: 4px 0;
    border: 1px solid #3c3c3c;
}

.ow_console_messages_btns .ow_console_messages_viewall a,
.ow_console_messages_btns .ow_console_messages_send a {
    background: #282828;
    color: #a2acb3;
}

.ow_table_1 tr.ow_tr_first th:last-child,
.ow_table_2 tr.ow_tr_first th:last-child {
    border-right-color: #3c3c3c;
}

.ow_console_divider {
    background: #3c3c3c;
}

.ow_anno {
    background-color: #282828;
    border: 1px solid #3c3c3c;
}

.ow_add_content:hover {
    background-color: #282828;
    color: #ffffff;
}

.ow_content_menu li.active {
    border-color: #3c3c3c #3c3c3c #3c3c3c #3c3c3c;
}

a.ow_lbutton,
span.ow_lbutton {
    background: #3c3c3c;
    border: 1px solid transparent;
}

.ow_ipc_info {
    border: 1px solid #3c3c3c;
}

.ow_forum_topic_posts > .ow_box.ow_no_cap .ow_ipc_info {
    border-color: #3c3c3c;
}

.toolbar {
    padding: 2px 1px;
}

.ow_border {
    border-style: solid;
    border-color: #3c3c3c;
}

.ow_lbutton:hover {
    border-color: #606060;
    cursor: pointer;
    text-decoration: none;
}

.ow_miniic_live .ow_lbutton.ow_green {
    color: #606060;
}

hr {
    background-color: #3c3c3c;
    border: none;
    height: 1px;
    margin: 5px 5px 15px 5px;
}

.ow_box_empty .ow_box_toolbar_cont {
    border-bottom: 2px solid #3c3c3c;
}

.ow_tip_box {
    background-color: #282828;
    color: #828894;
    border: 1px solid #ccd6de;
}

.ow_avatar_change {
    background: #282828e6;
}

.ow_console_list li:hover {
    border-color: #3c3c3c;
}

a.ow_lbutton,
span.ow_lbutton {
    background: #c3c3c3;
}
}
