@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("friendfeed.com") {

body, textarea, input {font: 11px Tahoma !important;}

#body, .textbox textarea {direction: rtl !important;}

#body .box-body {background: #fefefe !important;}

.header, .sharebox, .box-bar-text, .bottom, .feedformats {direction: ltr !important;}

.header .name {font-family: Arial !important;}

embed#fileuploader {display: none !important;}

.ebody .title .text {font-size: 13px !important;}

.picture {border: 1px solid #666 !important; padding: 1px !important;}

.sharebox, .entry, .pager.top {border-bottom:1px solid #aaa !important;}

.info {font-size: 10px !important; direction: ltr !important; text-align: right !important; background-position: right !important; padding-right: 12px !important;}

.likes {background-position: right !important; padding-right: 20px !important; direction: ltr !important; text-align: right !important;}

.expandcomment {direction: ltr !important; text-align: right !important;}

#feed .comment .content {margin-bottom: 5px !important;}

.entry .comment {font-size: 11px !important; line-height: 180% !important;}

.entry .comment .quote {float: right !important;}

.owner {background-color: #e9ffe5 !important; border: 1px solid #c5edbe !important; -moz-border-radius: 5px !important; -moz-box-shadow:1px 1px 10px #ccc; color: #000 !important;}

.entry .comment, .entry .commentform {background-position: right 4px !important; padding-right: 3px !important;}

