css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("img.4chan.org") {
body {
	font-size: 12pt !important;
	background: #FFFFEE !important;
	color: #800000 !important;
	padding-left: 5px !important;
	padding-right: 5px !important;
	margin-right: 0px !important;
	margin-left: 0px !important;
	margin-top: 5px !important;
}

img {
visibility: visible !important;
}

textarea, input {
	color: #000000 !important;
	background:#FFFFFF !important;
	border: 1px silver solid !important;
}

hr {
color:#000000 !important;
background:#000000 !important;
border: 0px #000000 solid !important;
}

a, a:visited {
	background: inherit !important;
	color: #0000EE !important;
	font-family: serif !important;
}

a:hover {
	color: #DD0000 !important;
	background: inherit !important;
	font-family: serif !important;
}

a.quotelink {
	background: inherit !important;
	color: #000080 !important;
	font-family: serif !important;
}

.logo {
	clear: both !important;
	text-align: center !important;
	background: inherit !important;
	font-size: 24pt !important;
	color: #800000 !important;
	width: 100% !important;
}

.postarea {
	background: inherit !important;
}

form {
	margin-top: 0px !important;
}

.rules {
	width: 468px !important;
	font-size: 10px !important;
	font-family: sans-serif !important;
}

.rules a, .rules a:visited, .rules a:link {
	font-family: sans-serif !important;
}

.rules li {
	margin-left: 1em !important;  
	text-indent: -1em !important;
}

.postblock {
	background: #EEAA88 !important;
	color: #800000 !important;
	font-weight: 800 !important;
}

.footer {
	text-align: center !important;
	font-size: 12px !important;
	font-family: serif !important;
}

.unkfunc {
	color: #789922 !important;
}

.filesize {
	font-size: 16px !important;
	font-family: serif !important;
	text-decoration: none !important;
}

.filesize span, span.postername, span.filetitle, span.commentpostername {
	unicode-bidi: embed !important;
}

.filetitle, .replytitle {
	background: inherit !important;
	font-size: 18px !important;
	font-family: serif !important;
	color: #CC1105 !important;
	font-weight: 800 !important;
}

.postername, .commentpostername {
	background: inherit !important;
	font-size: 16px !important;
	font-family: serif !important;
	color: #117743 !important;
	font-weight: 800 !important;
}

.postertrip {
	background: inherit !important;
	font-size: 16px !important;
	font-family: serif !important;
	color: #228854 !important;
}

.oldpost {
	background: inherit !important;
	font-family: serif !important;
	color: #f00000 !important;
	font-weight: 800 !important;
}

.omittedposts, .abbr {
	background: inherit !important;
	color: #707070 !important;
}

.reply {
	background: #F0E0D6 !important;
	color: #800000 !important;
	font-family: serif !important;
}

.replyhl {
	background: #F0C0B0 !important;
	color: #800000 !important;
	font-family: serif !important;
}

.doubledash {
	vertical-align: top !important;
	clear: both !important;
	float: left !important;
}

a.quotejs:active, a.quotejs:link, a.quotejs:visited {
	color: #800000 !important;
	text-decoration: none !important;
}

a.quotejs:hover {
	font-weight: bold !important;
}

.tn_thread {
	width: 200px !important;
	height: 100px !important;
	margin: 0px 20px 20px 20px !important;
	float: left !important;
	background: #eed !important;
	border: #ea8 1px solid !important;
	text-align: center !important;
}

.tn_reply {
	width: 100px !important;
	height: 100px !important;
	margin: 0px 20px 20px 20px !important;
	float: left !important;
	background: #eed !important;
	border: #ea8 1px solid !important;
	text-align: center !important;
}
}
