css:
@-moz-document domain("www.tumblr.com") {
	body#dashboard_index {
		background: #EEEEEE !important;
		width: 100% !important;
		overflow: -moz-scrollbars-vertical !important;
	}
	div#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
		background: #EEEEEE !important;
	}
	ol#posts {
		margin: 0!important;
	}
	li {
		background: #EEEEEE !important;
	}
	li.notification {
		background: #EEEEEE !important;
	}
	li.photo img, li.movie object {
		display: none;
	}
	div.so_ie_doesnt_treat_this_as_inline {
		visibility: hidden !important;
		width: 0 !important;
		height: 0 !important;
	}
	div#right_column {
		visibility: hidden !important;
		width: 0 !important;
		height: 0 !important;
	}
	div#left_column {
		width: 70% !important;
		float: none !important;
		margin: 0 auto !important;
	}
	div#container {
		width: 100% !important;
	}
	divdiv.l-header {
		display: none !important;
	}
	li#new_post {
		visibility: hidden !important;
		width: 0 !important;
		height: 0 !important;
	}
}
