@-moz-document domain(infoscreen.schulen-immenstadt.de) {
	 body{
        background-color:white
    }
    .kopf{
  		background-color: #0077B6;
		box-sizing: border-box;
		border-radius: 0px 0px 10px 10px;
	}
	.kopftitel a {
		font-weight: bold;
		color: rgb(255,255,255);
    }
 	.eineSpaltemitFusszeile{
		top: 50px;
	}
	.titellogo{
   		visibility: hidden;
      	height: 0;
     	overflow: visible;
        margin: 0;
	}
  	.titel{
   		font-weight: bold;
    	color: white;
    	background-color: #0077B6;
		padding: 10px;
		box-sizing: border-box;
		border-radius: 50px;
        margin-top: 50px;
  	}
  .datum{
   	    font-weight: bold;
    	color: white !important;
		padding: 8px !important;
		box-sizing: border-box;
		border-radius: 20px;
    	background-color:  #0077B6 !important;
  	}
	.aktiv {
    	color: white;
 		background-color: #0077B6;
	}
	.menupunktInhalt {
    	background-color:#0077B6;
	}
  	.infobox {
    	height: 0;
    	overflow: visible;
    	visibility: hidden;
	}


/* Buttons */

	.button {
  font-weight: bold;
  color: #000;
  background-color: #90E0EF;
  border: none;
  border-radius: 14px;
  outline: none;
}
.button:hover {
  box-shadow: 0 2px 4px -2px rgb(53 53 54 / 0.7);
}
.button:active {
  transform: translateY(-1px);
}

  button {
