css:
@-moz-document domain("www.clickcritters.com") {
body {
    background: url(https://www.clickcritters.com//images/layout/traptor_sky/wallpaper2.png) left top repeat, url(https://i.imgur.com/gL6mD3C.png) left top repeat-x;
    background-image: url(https://www.clickcritters.com//images/layout/traptor_sky/wallpaper2.png), url(https://i.imgur.com/2WoNXWr.png);
    background-position-x: left, left;
    background-position-y: top, top;
    background-size: initial, initial;
    background-repeat-x: repeat, repeat;
    background-repeat-y: repeat, no-repeat;
    background-attachment: initial, initial;
    background-origin: initial, initial;
    background-clip: initial, initial;
    background-color: #802231;
}

#megaWrapper {
    background-image: none;
}



#megaHeader {
    background-image: none;
}

#imgHeaderLeft {
    display: block;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    background: url('https://i.imgur.com/fJtDnwg.png') no-repeat;
    width: 1200px;
    height: 125px;
    padding-left: 1200px;
    margin-left: auto;
    margin-right: auto;
}



#imgHeaderRight {
    height: 0px;
}

textarea {
    font-size: 10pt;
    font-family: Verdana;
}

td,
p,
body {
    font-size: 8pt;
    font-family: Verdana;
    text-align: center;
}

a:hover {
    color: #6b4647;
    text-decoration: none;
}

#megaAd {
    width: 160px;
    background-color: #e0d0d0;
    vertical-align: top;
    margin-left: -50px;
}

#megaAd iframe {
    position: absolute;
    right: 0px;
    padding-top: 9px;
}

#megaTopNav,
#megaSearch {
    background-color: #73181a;
    background-image: url('https://i.imgur.com/J6TyPR0.jpg');
}

#megaSidebar {
    background-color: #874e52;
}

#megaUserinfo {
    background-color: #b79698;
    background-image: url('https://i.imgur.com/OAXkwKa.jpg');
    background-repeat: repeat-x;
}


#megaContent,
#megaFooter {

    background-color: #ba9395;
    /* The color of the background that is usually white is set here. White is #fff */
}

#megaFooter {
    padding-left: 15%;
}

#megaBottomLeft {
    background-color: #875154;
}

#megaBottomLeftImg {
    background-image: url('https://i.imgur.com/xpfRKjq.png');
}

#megaBottomRight {
    width: 160px;
    background-color: #ba9393;
}

#megaBottomRightImg {
    background-image: url('https://i.imgur.com/RbaNzo5.png')
}

h1 {
    background-color: #a84b4e;
    border: 2px solid #600c16;
}

#topNavLinks li ul.subnav {
    background: #a84b56;
    border: 2px solid #6b4647;
}

#topNavLinks li ul.subnav li {
    margin: 0;
    padding: 0;
    border-top: 1px solid #4f0a11;
    /*--Create bevel effect--*/
    border-bottom: 1px solid #4f0c13;
    /*--Create bevel effect--*/
    clear: both;
    width: 170px;
    height: 20px;
}

#topNavLinks li ul.subnavi li a {
    color: #fceaeb;
}

html #topNavLinks li ul.subnav li a:hover {
    background-color: #6b4647;
    color: #ebf7fc;
}

div.forum {
    color: #6b464a;
    background-image: url('https://i.imgur.com/drGdAyd.jpg');
    background-repeat: repeat-x;
    background-color: #a37576;
}

div.forum div {
    background-image: none;
}

div.forum a {
    color: #280e10;
}

div.category {
    vertical-align: center;
    background-color: #6b4647;
    background-image: url('https://i.imgur.com/MkwAHv5.jpg');
    background-repeat: repeat-x;
}

div.category div {
    background-color: #ba9397;
    background-image: none;
}

#forumtable tbody {
    background-color: #6b464a;
}

#forumtable tr.unread {
    background-color: #dbb6b7;
}

#forumtable tr .off {
    background-color: #ead7d8;
}

#forumtable tr a:hover {
    color: #fff;
    text-decoration: none;
}

#forumtable tr:hover {
    background-color: #b28588;
}

form input {
    background-color: #f7e6e7;
}

textarea,
select,
input {
    background-color: #fceaeb;
    font-family: verdana;
}

input[type=submit] {
    border-radius: 5px;
    background: linear-gradient(#fceaeb, #c6a1a2);
}

#mail_heading tr,
#mail_heading td {
    background-color: #d38185;
    border: solid 1px #49030a;
}
}
