css:
@-moz-document domain(magister.net)
{


  .head-bar
  {
    position: absolute;
    top: 56px;
    right: 10px;
    left: 0;
    z-index: 680;
    padding-left: 8px;
    height: 47px;
    line-height: 46px;
    color: #cca9af;
    border-bottom: none;
    background-color: #cca9af;
    font-size: 14px;
  }

  .content-container
  {
    position: relative;
    height: 100%;
    width: 100%;
    border: 1px solid #cca9af;
    background-color: #ffffff;
    text-align: left;
    overflow: auto;
    background-image: url("https://i.pinimg.com/736x/1b/4e/0f/1b4e0f14e6f960bed94db69666075020.jpg");
    background-size: 100%;
  }

  .block h3
  {
    position: relative;
    z-index: 100;
    margin: 0;
    padding: 0 0 0 8px;
    font: 14px "droid_sansregular", arial, helvetica, sans-serif;
    color: #cca9af;
    height: 44px;
    line-height: 44px;
    white-space: nowrap;
    overflow: hidden;
    -moz-text-overflow: ellipsis;
    -ms-text-overflow: ellipsis;
    text-overflow: ellipsis;
    border-bottom: 1px solid #cca9af;
    -moz-box-shadow: 0 2px 5px -1px rgba(156,138,152,255);
    -webkit-box-shadow: 0 2px 5px -1px rgba(156,138,152,255);
    box-shadow: 0 2px 5px -1px rgba(156,138,152,255);
  }

  .widget .agenda-list li.alert span.time:after
  {
    position: absolute;
    top: 7px;
    right: 8px;
    font-family: "magistersymbolsnormal";
    font-style: normal;
    font-weight: bold;
    font-size: 14px;
    speak: none;
    display: block;
    text-decoration: inherit;
    overflow: hidden;
    text-align: center;
    font-variant: normal;
    text-transform: none;
    content: 'Z';
    color: #e68ba2 !important;
  }

  .widget .agenda-list li.alert span.time:before
  {
    color: #cca9af;
    content: "wijziging";
  }

  .agenda-text-icon.outline
  {
    color: #e68ba2;
    background-color: #cca9af;
    border: 1px solid #cca9af;
    line-height: 18px;
  }

  .agenda-text-icon[icon-type="information"]
  {
    background-color: #cca9af;
    color: #cca9af;
  }

  .endlink a
  {
    display: block;
    float: right;
    padding: 0 8px;
    height: 40px;
    line-height: 40px;
    color: #cca9af;
    text-decoration: underline;
  }

  .alert
  {
    border-left: 4px solid #cca9af;
    background-color: #cca9af !important;
  }
}
