@-moz-document url-prefix("https://minestrator.com/panel/") {
/* ==UserStyle==
@name           Enhanced Minestrator Dark
@namespace      USO Archive
@author         Wasab_II
@description    `Un thème dark amélioré pour le panel Minestractor`
@version        20220412.22.37
@license        CC-BY-NC-4.0
@preprocessor   uso
==/UserStyle== */

body.dark-skin {
  background-color: #212225 !important;
  color: #8a99b5;
}
    
    .list-players {
  border: 0px solid;
  background-color: #23262d;
}
    
    .badge-player img {
	margin-right: 5px;
	width: 20px;
	height: 20px;
	border-radius: 15%;
}
    
    .badge-player {
        background-color: #2f333c ! important;
        color : #d4d9e3 ! important;
    }
    
    div + #extend-console {
        background-color: #23262d !important;
    }
    
    div + #extend-console > i {
        color: #fff !important;
    }

.theme-success .btn-danger {
  background-color: hsl(355, 84%, 61%);
  border-color: hsl(355, 84%, 61%);
  color: #ffffff;
}

.dark-skin .bg-light {
  background-color: #1b1d21 !important;
}

.dark-skin .box {
  box-shadow: none !important;
}

.dark-skin .input-group-text {
  border-color: #23262d;
  background-color: #23262d;
}

.dark-skin .dropdown-menu {
  background-color: #23262d;
  border-radius: 5px !important;
}

.dark-skin .card {
  background-color: #1b1d21;
}

.dark-skin .box .box-footer {
  background-color: #1b1d21;
  border-color: rgba(255, 255, 255, 0.12);
}

.dark-skin .form-control {
  background-color: #23262d ;
    border-color: #23262d;
}

.dark-skin .btn-default {
  background-color: #23262d;
    border-color: #23262d;
}

.dark-skin .progress {
  background-color: #23262d;
}

.dark-skin .nav-tabs .nav-link {
  color: #8c8d8f;
}

.dark-skin .nav-tabs-custom > .nav-tabs > li > a.active {
  background-color: #1b1d21;
    border-bottom: solid 1px #006046 !important;
  color: #37ae6b !important;
}

.dark-skin .wrapper {
  background-color: #212225;
    
}

.layout-top-nav.dark-skin .content-wrapper .content {
  background: #212225;
}
.dark-skin .bg-blackmod {
  background-color: #23262d !important;
}

.dark-skin .list-group-item {
  background-color: #23262d;
  border: none;
    border-radius: 10px;
    margin: 5px;
}

.main-header {
  margin-top: 0px !important;
  margin-bottom: 0px !important;
  padding: 15px 0px;
}

.layout-top-nav .main-header {
  width: 100%;
  margin: 0 auto;
    margin-top: 0px;
    margin-bottom: 0px;
  background-color: #1b1d21;
}

.dark-skin .nav-tabs-custom > .tab-content {
  background-color: #1b1d21;
}

.dark-skin .nav-tabs-custom {
  background-color: #1b1d21;
}

.col-server {
    background-color: #1b1d21 !important;
}

.dark-skin .box {
  background-color: #1b1d21;
  box-shadow: -7.829px 11.607px 21px 0px rgba(25, 42, 70, 0.13);
}

.layout-top-nav .main-nav {
  width: 100%;
}

.dark-skin .main-nav {
  background-color: #1b1d21;
border-radius: 0 !important;
    margin: 0;
}

.sm-blue a {
  color: #8c8d8f;
  border-radius: 15px 15px 0 0;
  background-color: transparent
}

.dark-skin.theme-success .sm-blue li.current > a, .dark-skin.theme-success .sm-blue li.highlighted > a {
  background: #212225;
  color: #37ae6b !important;
}

.dark-skin .content-header .page-title {
  color: #fff;
  border-color: #899bca;
}

.dark-skin .border-minesr {
  border-color: transparent !important;
}

body.dark-skin {
  color: #cacaca;
}
.dark-skin .btn-default {
  color: #cacaca;
}

.dark-skin h1, .dark-skin h2, .dark-skin h3, .dark-skin h4, .dark-skin h5, .dark-skin h6 {
  color: #787878;
}

.dark-skin .text-muted {
  color: #787878 !important;
}

.dark-skin .nav-tabs {
  border-color: transparent;
}

iframe{
  border-radius: 5px;
  border: solid 1px #212225;
}

.box-body {
  border-radius: 5px !important;
}

.box {
    border-radius: 5px;
}

.dark-skin .bg-minesr {
  background-color: #23262d !important;
}

.d3-area {
    stroke: #212225 !important;
    stroke-width: 1px;
    fill: #006046 !important;
}

#disk-usage-progress {
    background-color: #006046 !important;
}
}
