css:
@-moz-document url-prefix("https://www.livechart.me")
{
body {
   background-color: #222222;
   color: black;
}
.mark-fab, .anime-card, .considering .anime-card, .anime-card .related-links, .completed .anime-card, .watching .anime-card {
   background-color: #333333;
}
.anime-tags, .anime-date, .anime-studios, .anime-metadata {
	background-color: #333333;
}
.mark-fab:hover {
   background: #353535;
}
.episode-countdown,
.episode-countdown > time{
   color: black;
}
.callout{
   background-color: #333333;
	color: white;
}
.countdown-bar{ 
   background-color: #333333;
   color: #3b97fc;
}
}
