  html[dark] #buttons.ytd-masthead .ytd-masthead[is-icon-button]:nth-last-child(4):after
  {
    color: #fff;
  }

  ytd-app, html
  {
    background: url('https://www.colorhexa.com/ffffff.png') no-repeat;
    background-attachment: fixed;
    background-size: cover;
  }

  [dark] html, [dark] ytd-app
  {
    background: url(https://www.colorhexa.com/212121.png);
  }

  .more-button.ytd-video-secondary-info-renderer, .less-button.ytd-video-secondary-info-renderer
  {
    text-transform: none;
  }

/* General Parts */
  .ytp-chrome-bottom
  {
    bottom: -15px;
    padding-left: 25px;
    margin: 0px -37px;
  }

  .ytp-chrome-top,
.ytp-chrome-bottom
  {
    text-shadow: 0 0 0px rgba(0, 0, 0, .5);
  }

  .ytp-chrome-controls
  {
    background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAdCAYAAABrAQZpAAAABmJLR0QA/wD/AP+gvaeTAAAACXBIWXMAAABIAAAASABGyWs+AAAACXZwQWcAAAABAAAAHQAh4gKwAAAAMUlEQVQI12OQlJD4x2Sgr8/I9OTZs5dM//79Y2T6//8/IyqLAcLClEBjYahDKCHSKADN5VNQpdTxzgAAAABJRU5ErkJggg==);
    height: 25px;
  }

  .ytp-right-controls
  {
    height: 100%;
    background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAAdCAYAAABrAQZpAAAABmJLR0QA/wD/AP+gvaeTAAAACXBIWXMAAABIAAAASABGyWs+AAAACXZwQWcAAAABAAAAHQAh4gKwAAAAMUlEQVQI12OQlJD4x2Sgr8/I9OTZs5dM//79Y2T6//8/IyqLAcLClEBjYahDKCHSKADN5VNQpdTxzgAAAABJRU5ErkJggg==);
    margin: 0 -24px;
  }

  .ytp-time-display
  {
    font-size: 10.4px;
    font-family: Roboto, Helvicta, Arial, sans-serif;
    padding: 0 0px;
    width: 57px;
    margin-left: 8px;
    line-height: 27px;
  }

  .ytp-time-current
  {
    color: #B9B9B9!important;
  }

  .ytp-time-duration
  {
    color: #B9B9B9;
  }

  .ytp-time-separator
  {
    color: #b9b9b900;
  }

  .ytp-gradient-bottom
  {
    height: 0px;
    padding-top: 0px;
  }

  .ytp-settings-button.ytp-hd-quality-badge::after,
.ytp-settings-button.ytp-4k-quality-badge::after,
.ytp-settings-button.ytp-5k-quality-badge::after,
.ytp-settings-button.ytp-8k-quality-badge::after
  {
    top: 2px;
    right: 2px;
    background-color: #b50324;
  }

  .ytp-settings-button.ytp-hd-quality-badge::after
  {
    background: url(https://s.ytimg.com/yts/imgbin/player-common-vflbJjZA5.png);
    background-position: 26px 0px;
  }

  .ytp-big-mode .ytp-settings-button.ytp-hd-quality-badge::after
  {
    content: '___';
    background: url(https://s.ytimg.com/yts/imgbin/player-common-vflbJjZA5.png);
    background-position: 26px 0px;
    margin: -12px -16px;
    color: transparent;
    height: 7px;
  }

  .ytp-chrome-controls .ytp-button
  {
    height: 100%;
    border-right: 1px solid #222;
  }

  .ytp-bezel
  {
    border-radius: 10px;
  }

/* Scrubber */
  .ytp-scrubber-button.ytp-swatch-background-color
  {
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-common-vflXVjbFd.png) -158px -19px;
  }

  .ytp-scrubber-button.ytp-swatch-background-color:hover
  {
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-common-vflXVjbFd.png) -158px 0;
  }

  .ytp-scrubber-button
  {
    width: 13px;
    padding: 1.5px;
    margin-left: 0px;
    border-radius: 36.5px;
  }

/* Progress-Bar */
  .ytp-progress-bar-container:hover:not([aria-disabled=true])
  {
    cursor: pointer;
    height: 5px !important;
  }

  .ytp-swatch-background-color
  {
    background-image: linear-gradient(to bottom, #c00 0, #600 100%);
  }

  .ytp-progress-list
  {
    background: #1e1f1f;
    background-image: linear-gradient(to top, rgba(30, 31, 31, .75) 89%, #2f2f2f 90%);
  }

  .html5-progress-list, .ytp-progress-list, .video-ads .html5-progress-list.html5-ad-progress-list, .video-ads .ytp-progress-list.ytp-ad-progress-list
  {
    height: 7px !important;
  }

  .html5-progress-list:hover, .ytp-progress-list:hover, .video-ads:hover .html5-progress-list.html5-ad-progress-list:hover, .video-ads:hover .ytp-progress-list.ytp-ad-progress-list:hover
  {
    height: 7px !important;
  }

  .ytp-load-progress
  {
    background-image: linear-gradient(to top, #5a5a5a 89%, #666 90%);
  }

  .ytp-hover-progress-light
  {
    opacity: 0 !important;
  }

  .ytp-big-mode .ytp-scrubber-button
  {
    height: 13px !important;
    margin-left: 0px;
  }


/* Play */
  .ytp-chrome-controls .ytp-play-button
  {
    width: 0px;
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -96px -231px;
    padding: 0px 27.5px;
  }

  .ytp-chrome-controls .ytp-play-button:hover
  {
    width: 0px;
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -121px -42px;
    padding: 0px 27.5px;
  }


/* Pause */
  button.ytp-play-button.ytp-button[title="Pause (k)"]
  {
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -64px -177px;
  }

  button.ytp-play-button.ytp-button[aria-label="Pause (k)"]:hover
  {
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -64px -258px;
  }


/* Vol */
  button.ytp-mute-button.ytp-button
  {
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -153px -258px;
    width: 0px;
    margin-left: 2px;
    padding: 0 33px 0 2px;
  }

  button.ytp-mute-button.ytp-button:hover
  {
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -121px -123px;
    width: 0px;
    margin-left: 2px;
    padding: 0 33px 0 2px;
  }


/* Mute */
  button.ytp-mute-button.ytp-button[title="Unmute (m)"]
  {
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -121px -15px;
  }

  button.ytp-mute-button.ytp-button[title="Unmute (m)"]:hover
  {
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) 0 -42px;
  }


/* Next + Previous */
  a.ytp-next-button.ytp-button
  {
    display: none !important;
  }

  a.ytp-prev-button.ytp-button
  {
    display: none !important;
  }



/* Fullscreen */
  button.ytp-fullscreen-button.ytp-button
  {
    width: 0px;
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -89px -123px;
    padding: 0px 15px;
  }

  button.ytp-fullscreen-button.ytp-button:hover
  {
    width: 0px;
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -96px -96px;
    padding: 0px 15px;
  }


/* Exit Fullscreen */
  button.ytp-fullscreen-button.ytp-button[title="Exit full screen (f)"]
  {
    width: 0px;
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -1px -68px;
    padding: 0px 15px;
  }

  button.ytp-fullscreen-button.ytp-button[title="Exit full screen (f)"]:hover
  {
    width: 0px;
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -65px -149px;
    padding: 0px 15px;
  }


/* Theater Mode */
  button.ytp-size-button.ytp-button[title="Theater mode (t)"]
  {
    width: 0px;
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -89px -15px;
    background-size: auto;
    padding: 0 14.5px;
  }

  button.ytp-size-button.ytp-button:hover
  {
    width: 0px;
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -32px -204px;
    background-size: auto;
    padding: 0 14.5px;
  }


/* Default Mode */
  button.ytp-size-button.ytp-button[title="Default view (t)"]
  {
    width: 0px;
    padding: 0 14.5px;
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -57px -123px;
    background-size: auto;
  }

  button.ytp-size-button.ytp-button[title="Default view (t)"]:focus:hover
  {
    width: 0px;
    padding: 0 14.5px;
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -32px -231px;
    background-size: auto;
  }


/* Settings */
  button.ytp-button.ytp-settings-button
  {
    width: 0px;
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -153px -123px;
    padding: 0 14.5px;
    background-size: auto;
  }

  button.ytp-button.ytp-settings-button:hover
  {
    width: 0px;
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -128px -204px;
    padding: 0 14.5px;
    background-size: auto;
  }

  button.ytp-button.ytp-settings-button:active
  {
    width: 0px;
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -128px -204px;
    padding: 0 14.5px;
    background-size: auto;
  }


/* Captions */
  button.ytp-subtitles-button.ytp-button
  {
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -65px -204px;
    width: 0px;
    padding: 0 14.5px;
    background-size: auto;
  }

  button.ytp-subtitles-button.ytp-button:hover
  {
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -65px -285px;
    width: 0px;
    padding: 0 14.5px;
    background-size: auto;
  }

  .ytp-chrome-controls .ytp-button[aria-pressed=true]:after
  {
    width: 0px;
    left: 9px;
    -moz-transition: left .25s cubic-bezier(0.0, 0.0, 0.2, 1), width .25s cubic-bezier(0.0, 0.0, 0.2, 1);
    -webkit-transition: left .25s cubic-bezier(0.0, 0.0, 0.2, 1), width .25s cubic-bezier(0.0, 0.0, 0.2, 1);
    transition: left .25s cubic-bezier(0.0, 0.0, 0.2, 1), width .25s cubic-bezier(0.0, 0.0, 0.2, 1);
  }

  button.ytp-subtitles-button.ytp-button[aria-pressed="true"]
  {
    background: url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -33px -150px;
    width: 0px;
    padding: 0 14.5px;
    background-size: auto;
  }

  button.ytp-subtitles-button.ytp-button[aria-pressed="true"]:hover
  {
    background: url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -33px -150px;
    width: 0px;
    padding: 0 14.5px;
    background-size: auto;
  }

/* MiniPlayer (for users of the material design) */
  button.ytp-miniplayer-button.ytp-button
  {
    width: 0px;
    padding: 0 14.5px;
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -57px -123px;
    background-size: auto;
  }

  button.ytp-miniplayer-button.ytp-button:hover
  {
    width: 0px;
    padding: 0 14.5px;
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -32px -231px;
    background-size: auto;
  }


/* Volume Slider */
  .ytp-volume-slider-handle:before
  {
    left: -64px;
    background: url(data:image/png;base64,);
    background-position: 16px !important;
    height: 32px;
  }

  .ytp-volume-slider-handle:after
  {
    left: 5px;
    background: url(data:image/png;base64,) !important;
    background-position: 151px !important;
    height: 32px;
  }

  .ytp-volume-slider-handle
  {
    position: absolute;
    top: 13px;
    width: 5px;
    height: 14px;
    border-radius: 0px;
    margin-top: -5.9px;
    background: url(data:image/png;base64,) 258px 0px !important;
  }

  .ytp-volume-slider-handle[style="left: 0px;"]
  {
    left: -9px !important;
  }

  .ytp-volume-slider-active .ytp-volume-panel
  {
    width: 54px;
    margin-right: 3px;
  }

  .ytp-volume-slider-handle
  {
    margin-left: 9px;
  }


/* Settings Box */

  .ytp-menuitem
  {
    height: 23px;
  }

  .ytp-menuitem-toggle-checkbox
  {
    border-radius: 0px;
  }

  .ytp-popup
  {
    text-shadow: 0 0 2px rgba(255, 255, 255, 0);
  }

  .ytp-menu-label-secondary
  {
    font-size: 91%;
    color: #a2a2a2;
  }

/* Right-Click Menu */
  .ytp-panel-menu[style="height: 174px;"]
  {
    color: #333;
    margin-left: -32px;
  }

  .ytp-menuitem-label
  {
    font-size: 118%;
    font-weight: 100;
  }

  .ytp-menuitem[data-visual-id="9"]
  {
    height: 25px;
  }

  .ytp-panel[style="width: 295px; height: 174px;"]
  {
    bottom: -4px;
    right: 0px;
    width: 295px !important;
    height: 180px !important;
  }

  .ytp-menuitem-label,
.ytp-menuitem-content
  {
    display: table-cell;
    vertical-align: middle;
    padding: 2px 26px;
  }

  .ytp-panel-menu[style="height: 177px;"]
  {
    color: #333;
  }

  .ytp-panel-menu[style="height: 178px;"]
  {
    background: #333;
    color: #ffffff;
  }

  .ytp-menuitem-label,
.ytp-menuitem-content
  {
    display: table-cell;
    vertical-align: middle;
    padding: 2px 26px;
  }

/* Fullscreen Mode */
  .ytp-big-mode .ytp-chrome-top .ytp-button,
.ytp-big-mode .ytp-chrome-controls .ytp-button
  {
    width: 0px !important;
  }

  .ytp-big-mode .ytp-time-display
  {
    line-height: 27px;
  }

  .ytp-big-mode .ytp-chrome-controls
  {
    height: 27px;
    line-height: 27px;
  }

  .ytp-big-mode .ytp-chrome-bottom
  {
    height: 54px;
    padding-top: 4px;
    margin-bottom: -14px !important;
  }

  .ytp-big-mode .ytp-progress-list
  {
    transform: scaleY(0.625) scaleX(1.018) translateX(12px) !important;
  }

  .ytp-big-mode .ytp-progress-bar-container:hover:not([aria-disabled="true"]) .ytp-progress-list
  {
    transform: translateY(-2.5px) scaleX(1.018) translateX(12px) !important;
  }

/* Fullscreen Mode Volume Slider */
  .ytp-big-mode .ytp-volume-slider-handle:before,
.ytp-big-mode .ytp-volume-slider-handle:after
  {
    height: 14px;
    margin-top: -5px;
    width: 85px;
  }

  .ytp-big-mode .ytp-volume-slider-handle
  {
    width: 0px;
    height: 18px;
    border-radius: 9px;
    margin-top: -9px;
  }

  .ytp-big-mode .ytp-volume-slider-handle:before
  {
    left: -64px;
  }

  .ytp-big-mode .ytp-volume-slider-active .ytp-volume-panel
  {
    width: 55px;
    margin-right: 0px;
  }

  .ytp-big-mode .ytp-volume-slider-handle[style="left: 0px;"]
  {
    left: 11px !important;
  }

  .ytp-big-mode .ytp-volume-slider-handle
  {
    margin-left: -11px;
  }

/* Replay */
  .ytp-chrome-controls .ytp-play-button[title="Replay"]
  {
    width: 0px;
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -96px -285px;
    padding: 0px 27.5px;
  }

  .ytp-chrome-controls .ytp-play-button[title="Replay"]:hover
  {
    width: 0px;
    background: no-repeat url(http://s.ytimg.com/yt/imgbin/player-dark-vflCDBE54.png) -32px -15px;
    padding: 0px 27.5px;
  }

  #search-icon-legacy
  {
    background: linear-gradient(to bottom, #FFFFFF 10%, #F0F0F0 100%) !important;
    color: #000;
    font-size: 13px;
    border: solid thin #767676;
    border-radius: 0px;
    transition-duration: 0.0s;
    fill: #000;
  }

  #search-icon-legacy:hover
  {
    background: linear-gradient(to bottom, #FFFFFF 10%, #ECECEC 100%) !important;
    color: #000;
    font-size: 13px;
    border: solid thin #4F4F4F;
    border-radius: 0px;
    transition-duration: 0.0s;
    fill: #000;
  }

  [dark] #search-icon-legacy
  {
    background: linear-gradient(to bottom, #5e5e5e 10%, #353535 100%) !important;
    color: #000;
    font-size: 13px;
    border: solid thin #767676;
    border-radius: 0px;
    transition-duration: 0.0s;
    fill: #000;
  }

  [dark] #search-icon-legacy:hover
  {
    background: linear-gradient(to bottom, #5e5e5e 10%, #303030 100%) !important;
    color: #000;
    font-size: 13px;
    border: solid thin #767676;
    border-radius: 0px;
    transition-duration: 0.0s;
    fill: #000;
  }

  #masthead
  {
    width: 1356px;
    left: 278px;
    position: relative;
  }

  .ytp-miniplayer-button
  {
    display: none!important;
  }

