css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("answers.yahoo.com") {

/* GROUPED BORDERS */
#y-ks-whole-page,#ks-homepage-heading,#mini-c,#content,#y-ks-banner,#ks-homepage-questions,h2,#ks-user-activity-detail-list,#y-ks-mini-banner,div.middle,div.left,div.right,#yan-banner,#yan-earn-points,li.first-child,ul,#yan-breadcrumbs,li.vcard,#yan-leaderboard,#yan-question-tools,#yan-answer-search-inline,li span,a,input.button,#yan-answers-blog-info,#yan-leaderboard tr.first,#yan-leaderboard thead tr,div.bd,#yan-nav-browse-menu,#yan-nav-my-activity-menu,#yan-nav-about-menu
{border:none !important}

#y-ks-profile-widget,table.question-results,div.global-search-bar,div.sidebar-box,#boa,#user-profile-content,div.tab-content,div.delimiter,#bar-answers-best,#bar-answers-total,#yan-header-searchbox,ul.questions,#yan-categories,#yan-persistent-links,#yan-filter,#yan-main,#yan-starthis,li.mail-friend,#yan-save-question,#yan-mystatus,#yan-comment-link,#cat-undo,li.rate-up,li.rate-down,li.flag-abuse,form.ask-flow,#yan-reportthis,#bar-questions-knowledge,#bar-questions-voting,#bar-questions-open,#flag-abuse,div.content p a,#yan-bestof
{border:1px solid yellow !important}

div.corner-top,div.corner-bottom,#pa-nav,#yan-vitality-setting,table[cellspacing="9"],img[src="http://l.yimg.com/a/i/us/sch/gr/wallpaper_thumbnail.jpg"],#yan-banner-ask p,#yan-banner-answer p,#yan-banner-discover p
{display:none !important}

#yan-leaderboard li.first,#yan-leaderboard li,div.question-details,div.answer-details,#up-member-edit-box
{border-bottom:1px dotted yellow !important}

#yan-question-tools,a.current,div.other-questions,#yan-extend-question,#yan-report-quiz,#yan-choose-best-answer
{border-top:1px solid yellow !important}

li.rate-up,li.rate-down
{border-right:none !important}

li.rate-down,li.flag-abuse,#up-member-stats-right
{border-left:1px dotted yellow !important}

div.answer,#up-member-edit-box,#yan-answer-search-inline,#yan-leaderboard tr
{border-top:1px dotted yellow !important}

tr.question,li.alt
{border-bottom:1px dashed yellow !important}

li.alt,h1.formFields,#y-ks-footer-intl-links
{border-top:1px dashed yellow !important}

#yan-my-link,div.side-link
{border-left:1px solid yellow !important}





/* FONT DETAILS */
h1,h3,#yan-answer-answer,#yan-answer-search,textarea,dt,div,li,h2,p,td,input[type="text"],#up-member-since,em,strong,span.detail-type,label[for="yan-showquestions"]
{color:#CCCCCC !important}

span.detail-value
{color:#8F8F8F !important}

#bar-answers-total
{color:white !important}

input.button
{color:black !important}

span.best-answer-notify
{color:green !important}

li.rate-up span.rated
{color:green !important}

li.rate-down span.rated
{color:red !important}

#ks-user-profile-watchlist-tab a,#ks-user-profile-starred-tab a,#ks-user-profile-questions-tab a,#ks-user-profile-answers-tab a,#ks-user-profile-summary-list-tab a,#ks-user-profile-details-tab a,#ks-user-profile-contacts-list-tab a,#ks-user-profile-fans-list-tab a,#ks-user-profile-blocked-list-tab a
{color:black !important;
font-weight:bolder !important}



/* BACKGROUNDS */
body,div,h1,#yan-question-tools,li,form.ask-flow,fieldset,#yan-reportthis,p.bd,li span,#flag-abuse,li a,p,div.content p a,form[method="get"],#yan-leaderboard td
{background-image:none !important;
background-color:black !important}
table,h2,td,ul,span,dd,strong,#no-results-found
{background-color:black !important}
input.button
{background-image:none !important;
background-color:yellow !important}
img[src="http://l.yimg.com/a/i/us/sch/gr/ptwidget_default_icon.gif"]
{visibility:hidden !important}

#ks-user-profile-watchlist-tab a,#ks-user-profile-starred-tab a,#ks-user-profile-questions-tab a,#ks-user-profile-answers-tab a,#ks-user-profile-summary-list-tab a,#ks-user-profile-details-tab a,#ks-user-profile-contacts-list-tab a,#ks-user-profile-fans-list-tab a,#ks-user-profile-blocked-list-tab a
{background-color:yellow !important;
opacity:0.5 !important}

#ks-user-profile-answers-tab.selected a,#ks-user-profile-questions-tab.selected a,#ks-user-profile-watchlist-tab.selected a,#ks-user-profile-starred-tab.selected a,#ks-user-profile-summary-list-tab.selected a,#ks-user-profile-details-tab.selected a,#ks-user-profile-contacts-list-tab.selected a,#ks-user-profile-fans-list-tab.selected a,#ks-user-profile-blocked-list-tab.selected a
{background-image:none !important;
background-color:yellow !important;
opacity:1 !important}

#yan-nav-browse-menu div.bd,#yan-nav-my-activity div.bd,#yan-nav-about-menu div.bd
{background-color:#080808 !important}

#yan-nav-browse-menu a:link,#yan-nav-browse-menu a:visited,#yan-nav-browse-menu li,#yan-nav-browse-menu ul,#yan-nav-my-activity a:link,#yan-nav-my-activity a:visited,#yan-nav-my-activity li,#yan-nav-my-activity ul,#yan-nav-about-menu a:link,#yan-nav-about-menu a:visited,#yan-nav-about-menu li,yan-nav-about-menu ul
{background-color:transparent !important}





/* LINKS */
a:link,a:visited
{color:#8F8F8F !important}
a:hover,#yan-nav-browse-menu a:hover,#yan-nav-my-activity-menu a:hover,#yan-nav-about-menu a:hover
{text-decoration:none !important;
background-color:#303030 !important}




/* EXTRA STUFF */
#y-ks-header-user-profile-widget-msg-1
{height:50px !important}
div.side-link
{height:28px !important}
#psearch
{padding:0 !important}
a[target="_top"]
{border:1px solid grey !important}
#up-member-stats-content
{border:1px dashed yellow !important}
#cat-undo
{padding-bottom:3px !important}
div.yan-content-heading
{border-bottom:none !important}
a.current
{background-color:#212121 !important;
border-bottom:1px solid yellow !important}
#user-profile-left
{border-right:1px dotted yellow !important}
#up-member-about-text
{padding-right:4px !important}
#starred-tab-content table.question-results
{border:none !important}
li[class="vcard alt show"]
{border:none !important}
.short #yan-banner-ask p
{display:inherit !important}
}
