css:
@-moz-document domain("www.nitrotype.com")
{
  body
  {
    background-color: #2d2e2e;
    overflow-y: scroll;
    background: none;
    overflow-x: hidden;
    position: relative;
  }

  .card--grit
  {
    background-image: url("https://i.imgur.com/OCUWjGK.png");
    background: fixed;
  }

  html
  {
    background: fixed url("https://c4.wallpaperflare.com/wallpaper/500/442/354/outrun-vaporwave-hd-wallpaper-preview.jpg") no-repeat, #2d2e2e;
    box-sizing: border-box;
    color: #2d2e2e;
    font-family: "Montserrat", sans-serif;
    font-size: 16px;
    font-weight: 300;
    line-height: 1.6;
    background-size: 120%;
    background-position: center;
  }

  .bg--gradient
  {
    background: fixed;
  }

  .card--a
  {
    background-image: url(https://c4.wallpaperflare.com/wallpaper/586/675/740/vaporwave-wallpaper-preview.jpg);
    color: #ffffff;
    background-size: 150%;
    background-position: center;
  }

  .btn--primary
  {
    background: #0c0c0c;
    color: #ffffff;
  }

  .theme--pDefault .profile-title
  {
    color: #FFFFFF;
    text-shadow: 0 2px 2px rgba(2, 2, 2, 0.25);
  }

  .theme--pDefault .profile-levelNum
  {
    color: #FFFFFF;
    text-shadow: 0 2px 2px rgba(2, 2, 2, 0.25);
  }

  .tc-t
  {
    color: #ffffff;
  }

  .theme--pDefault .profile-body
  {
    color: #FFFFFF;
  }
}

@-moz-document domain(nitrotype.com)
{


  .dash-copyContainer
  {
    background: #1d1e23;
    -webkit-box-shadow: 0px 1px 157px 10px rgba(0,0,0,0.78);
    box-shadow: 0px 1px 157px 10px rgba(0,0,0,0.78);
    border-radius: 5px;
    flex: 1;
    overflow: hidden;
    padding: 15px;
    display: flex;
  }

  .dash-copy
  {
    color: #FFFFFF;
    font-family: "Roboto Mono", "Courier New", Courier, "Lucida Sans Typewriter", "Lucida Typewriter", monospace;
    font-size: 28px;
  }

  ::selection
  {
    background-color: #1976c6;
    color: white;
  }

  .tsxxs
  {
    font-size: 10px;
  }

  .bg--gradient
  {
    background: fixed;
  }

  .theme--pDefault .profile-bg
  {
    background: url("https://img5.goodfon.com/wallpaper/nbig/6/1b/synth-retrowave-synthwave-fon-new-retro-wave-sintveiv-ret-15.jpg") top left repeat, linear-gradient(0deg, #2d2e2e 60%, #ffffff 100%);
    color: #d3d3d3;
  }

  .btn--inky, .btn--inky:focus:not(:hover)
  {
    color: #ffffff;
  }

  .card--shadow
  {
    box-shadow: none;
  }

  .news--solo .news-body p
  {
    color: #ffffff;
    line-height: 1.8;
  }

  .news-teaser
  {
    color: #ffffff;
    margin-bottom: 40px;
  }

  .theme--pDefault .profile-bgHead
  {
    background: url("https://img5.goodfon.com/wallpaper/nbig/6/1b/synth-retrowave-synthwave-fon-new-retro-wave-sintveiv-ret-15.jpg") top left repeat, linear-gradient(0deg, #2d2e2e 10%, #ffffff 100%);
  }

  .theme--pDefault.is-gold .profile-bg
  {
    background: url("https://img5.goodfon.com/wallpaper/nbig/6/1b/synth-retrowave-synthwave-fon-new-retro-wave-sintveiv-ret-15.jpg") top left repeat, linear-gradient(0deg, #2d2e2e 10%, #ffffff 100%);
  }

  .theme--pDefault.is-guest .profile-bgHead
  {
    background: #fff0;
  }
}
