css:
@-moz-document url-prefix(http://img.4chan.org/b/) {

input, hr, textarea
{
background-color: white !important;
border: 1px grey solid !important;
color: black !important;
}

img
{
visibility: visible !important;
}

body {
	font-size: 12pt !important;
	background: #EEF2FF !important;
	color: #000000 !important;
	padding-left: 5px !important;
	padding-right: 5px !important;
	margin-right: 0px !important;
	margin-left: 0px !important;
	margin-top: 5px !important;
}

a, a:visited {
	background: inherit !important;
	color: #34345C !important;
	font-family: serif !important;
}

a:hover {
	color: #DD0000 !important;
	background: inherit !important;
	font-family: serif !important;
}

a.quotelink {
	background: inherit !important;
	color: #DD0000 !important;
	font-family: serif !important;
}

.logo {
	clear: both !important;
	text-align: center !important;
	background: inherit !important;
	font-size: 24pt !important;
	color: #AF0A0F !important;
	width: 100% !important;
}

.postarea {
	background: inherit !important;
}

form {
	margin-top: 0px !important;
}

.rules {
	width: 468px !important;
	font-size: 10px !important;
	font-family: sans-serif !important;
}

.rules a, .rules a:visited, .rules a:link {
	font-family: sans-serif !important;
}

.rules li {
	margin-left: 1em !important;
	text-indent: -1em !important;
}

.postblock {
	background: #9988EE !important;
	color: #000000 !important;
	font-weight: 800 !important;
}

.footer {
	text-align: center !important;
	font-size: 12px !important;
	font-family: serif !important;
}

.unkfunc {
	color: #789922 !important;
}

.filesize {
	font-size: 16px !important;
	font-family: serif !important;
	text-decoration: none !important;
}

.filesize span, span.postername, span.filetitle, span.commentpostername {
	unicode-bidi: embed !important;
}

.filetitle, .replytitle {
	background: inherit !important;
	font-size: 18px !important;
	font-family: serif !important;
	color: #0F0C5D !important;
	font-weight: 800 !important;
}

.postername, .commentpostername {
	background: inherit !important;
	font-size: 16px !important;
	font-family: serif !important;
	color: #117743 !important;
	font-weight: 800 !important;
}

.postertrip {
	background: inherit !important;
	font-size: 16px !important;
	font-family: serif !important;
	color: #228854 !important;
}

.oldpost {
	background: inherit !important;
	font-family: serif !important;
	color: #0F0C5D !important;
	font-weight: 800 !important;
}

.omittedposts, .abbr {
	background: inherit !important;
	color: #070707 !important;
}

.reply {
	background: #D6DAF0 !important;
	color: #000000 !important;
	font-family: serif !important;
}

.replyhl {
	background: #D6BAD0 !important;
	color: #000000 !important;
	font-family: serif !important;
}

.doubledash {
	vertical-align: top;
	clear: both;
	float: left;
}

a.quotejs:active, a.quotejs:link, a.quotejs:visited {
	color: #000000 !important;
	text-decoration: none !important;
}

a.quotejs:hover {
	font-weight: bold !important;
}

.tn_thread {
	width: 200px !important;
	height: 100px !important;
	margin: 0px 20px 20px 20px !important;
	float: left !important;
	background: #eef2ff !important;
	border: #98e 1px solid !important;
	text-align: center !important;
}

.tn_reply {
	width: 100px !important;
	height: 100px !important;
	margin: 0px 20px 20px 20px !important;
	float: left !important;
	background: #eef2ff !important;
	border: #98e 1px solid !important;
	text-align: center;
}

#ad {
	width: 120px;
	margin: 0 !important;
	padding: 0 !important;
	position: absolute !important;
	right: 1em !important;
	border: 1px solid #98E !important;
	font-family: arial,helvetica, sans-serif !important;
	font-size: 11px !important;
}

#ad div {
	margin: 0 !important;
	padding: 0.4em !important;
}

#ad div.ad-title {
	padding: 0em;
	background: #98E !important;
	color: #000000 !important;
	font-size: 11px !important;
}

#ad div.ad-title a, .bottomAdFoot a, .bottomAdFoot a:hover {
	font-family: arial,helvetica, sans-serif !important;
	color: #000000 !important;
}

#ad div.ad-text a {
	font-family: arial,helvetica, sans-serif !important;
}

.adHeadline {
	font-family: arial,helvetica, sans-serif;
	font-size: 11px !important;
	font-weight: normal !important;
}

.adText {
	font-family: arial,helvetica, sans-serif;
	font-size: 11px !important;
	font-weight: normal !important;
	text-decoration: none !important;
	color: #000 !important;
}

.bottomAdTitle {
	font-family: arial,helvetica, sans-serif;
	background: #98E !important;
	color: #000000 !important;
}

#bottomAd {
	height: 52px;
	font-size: 11px !important;
}

#bottomAdOuter {
	width: 600px;
	border: 1px solid #98E !important;
	font-size: 11px !important;
}

.spoiler a.quotelink, .spoiler .unkfunc {
	color: inherit !important;
}

.exif {
	display: none;
	font-family: arial,helvetica, sans-serif !important;
	font-size: xx-small !important;
}

.exif td {
	font-family: arial,helvetica, sans-serif !important;
	font-size: xx-small !important;
}

.exif td b {
	font-weight: 800 !important;
	text-decoration: underline !important;
	font-size: x-small !important;
}

#header {
	position: absolute !important;
	left: 5px !important;
	right: 5px !important;
}

* html #header {
	width: 100% !important;
}

#navtop {
	left: 0px !important;
	float: left !important;
	font-size: 11pt !important;
}

#navtopr {
	right: 0px !important;
	display: block !important;
	text-align: right !important;
	font-size: 11pt !important;
}


}
