@-moz-document domain("schauauf.linz.at") {
/* general */
body {
  font-size: 1.2em !important;
  min-width: unset;

}
@media only screen and (max-width: 1000px) {
  #main {
    display: flex;
    flex-flow: column-reverse;
  }
  #content {
    width: auto;
  }
  #twocolumn {
    width: auto;
    display: flex;
    flex-flow: column;
  }
  #twocolumn > div {
    width: auto !important;
  }
  #twocolumn div#map-container-detail {
    /* wider but give it some space to allow scrolling past it */
    margin: 0 auto;
    width: 90%;
    height: auto;
  }
}

#bg-top {
  display: flex;
}
#bg-top .dashboard { 
  position: relative
}
#bg-top .dashboard .tools {
  left: unset;
  right: 1em;
  top: 1em
}
}

@-moz-document regexp(".*\\/\\/schauauf.linz.at\\/meldung_erstellen\\/index.html\\??.*") {
/* general */
body {
  font-size: 1.2em !important;
}

/* narrow screens */
@media only screen and (max-width: 1000px) {

  /* sidebar */
  #main > div {
    float: none;
    width: auto;
  }
  

  /* $#%! */
  div#map-create {
    width: auto;
  }
  
  #createMessageForm .webform_label, #createMessageForm .webform_field {
    width: auto !important;
    float: none;
  }
}
}
