css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.explosm.net/comics") {
/*
  body,
  table[background="http://www.explosm.net/images/prdy/background.gif"],
  div#maincontent div div
  {
	background-color:  black !important; 
  }
  div { 
	border-style: hidden !important;
  }

  #maincontent table[height="168"],  #thebodycontent table tbody tr td div,  #thebodycontent table[width="240"],  #thebodycontent table tr td[width="240"],  #thebodycontent table tr td[width="*"], iframe, #footer, div#maincontent div table,
div#adbar, div#networkbar, div#logobottom, 

div#maincontent div:nth-of-type(1) img,
*/

  div#maincontent div div
  {
    background-color:  black !important; 
    border: none !important;
  }


  div div img
  {
    background-color:  white !important; 
  }

#menubar div#logo, 
div#maincontent div div:nth-child(1),
#logobottom,
#sidebar,
div#maincontent div table:nth-last-child(1),
/* div#maincontent div > table, */
div#maincontent div > b,
div#maincontent div > br,
#adbar, #networkbar, iframe,
a[target="_blank"]
  {
    display: none !important;
  }

#menubar, #menu, #menu a, #menu img
  {
    height:  20px !important;
    padding: 0 5px 0 0 !important;
    vertical-align: top !important;
    width:  auto !important;
  }


#header, body {
  background-color:  black !important;
  background-image:  none !important;
  color:  #fff !important;
}
* {
  background-color:  black;
  color:  #fff !important;
}

/*
  #thebodycontent { 
	background:black none repeat scroll 0 0 !important; 
	margin: 0 !important; 
	width: auto !important; 
  }
#menu
  {
    float: left !important;
  }
*/

}

@-moz-document domain("penny-arcade.com") {

html, body, .content {  
	background: #000000 !important; 
	}

#funding-h, #header, .header, .float_right {
	display: none !important;
	}

}

@-moz-document domain("smbc-comics.com") {

td[width="235"],
img[alt="header"],
img[src$="/images/SMBCTheaterAd.png"],
img[src^="http://www.smbc-comics.com/comics"] + br + table tbody tr td table,
img[src^="http://www.smbc-comics.com/comics"] + br + table + *,
img[src^="http://www.smbc-comics.com/comics"] + br + table ~ *,
/*,
 img[src="http://www.smbc-comics.com/images/secretvoteybutton.gif"]
*/
#refbox,
form[target="paypal"],
a[name="blog"],
a[name="blog"] ~ *,
font[size="2"]
  {
  display:  none !important;
  }

body
  {
    background-color:  #111 !important;
  }
* 
  {
    background-image:  none !important;
  }

}

@-moz-document 	domain("xkcd.com"){

table, h1, h2, h3, h4, #topContainer, .line, #comicLinks, #licenseText, .dialog .hd .c, .dialog .ft .c, .lj, #middleFooter .bd .c .s a {
	display : none; 
}

img {
   padding-bottom:5px;
   padding-left:5px;
   padding-right:5px;
   padding-top:5px;
}

#container{
	width: 780px;
	margin-left: auto;
	margin-right: auto;
	padding-bottom: 0px;	
	font-size:0px;
}

#middleContent ul{
	margin: auto;
	padding: 32px;
	list-style-type: none;
	width: auto;
}
#middleContent li{float:left;}

#middleContent ul li a{
	display: block;
	text-align:center;
	margin: 5px 15px 5px 15px;
	width: 70px;
	border: 0px solid black;
	color: #FFF;
	text-decoration: none;
	font-size: 16px;
	font-variant: small-caps;	
	font-family: Lucida, sans-serif;
	font-weight:600;
}

.dialog .bd .c .s {
	margin: 0px 0px 0px 0px !important;
}

#ohnorobot {
        margin-top:25px;
        padding:5px;
}

}
