css:
@namespace url(http://www.w3.org/1999/xhtml);


@-moz-document domain("askmen.com") {

#header, 
#navigation,
#footer,
#special_feature,
div.tools,
.dlIphone,
.gillette,
.more_top10,
.top10_sections,
.newsletterSignUpArticle,
.shareThisArticle,
.similarArticles,
.comments,
#breadcrumb
  {
    display:  none !important;
  }

.article
  {
    width: auto !important;
/*
    padding: 0px 10% 0px 10% !important;
*/
  }

body
  {
    background: #484848 none repeat scroll 0 0 !important;
  }

}

@-moz-document domain("cio.com") {

#breadcrumb,
#related_section,
/*
#upperTopNav,
#upperCenterTopNav,
#drilldownNavLeft,
#drilldownNavRight,
div#drilldownNav div:nth-of-type(1), 
*/
#topnav
  {
    display: none !important;
  }

/*
  #drilldownNav
  {
    width: 100% !important;
  }
  #topnav {
    height:  auto !important;
  }
*/
}

@-moz-document domain("cracked.com") {

#tyntPrint,
#TopBar,
#Header,
#NavAndTools,
#Tombstone,
#FooterLinks,
#NewsletterAndSearch, 
#Footer,
#footTrack,
#footTrack ~ *,
#SecondaryContent,
#Breadcrumbs,
#Ad990,
#NewArticles,
#Recommended
  {
    display: none !important;
  }

}

@-moz-document domain("dailymail.co.uk") {

.page-header,
div.article-advert-body div.beta,
.and-footer,
.page-footer,
.relatedItems,
.relatedItemsTopBorder,
.article-icon-links-container
  {
    display: none !important;
  }

.article-text 
  {
    margin-right: 100px !important;
    width:  auto !important;
  }

.page-banner
  {
    padding-top:  0px !important;
  }
}

@-moz-document domain("articles.dailypress.com") {

#area-top,
#area-left,
h2.kicker,
.ad_header,
.ad_header + div,
#area-bottom
  {
    display: none !important;
  }
}

@-moz-document domain("www.eff.org") {
  body {
     background-image: none !important;
  /*   background-color: white !important; */
  }

  #hide, #topnav, body>div:first-child, #footer, #enditems, 
  #sidebar, .breadcrumb, .topics, .deeplinkspost>p:last-child, #sidebar_right
    { display: none !important; }

  #upper_level_containers, .wrapper, #contentcontainer, #content, .blog
  {
     width: 100% !important;
     max-width: none !important;
     margin: 0 !important;
     padding: 0 !important;
     float: none !important;
  }

  #primaryContent, .deeplinkspost
  {
     padding: 2% !important;
     width: 96% !important;
     text-align: justify !important;
     /*
     font-size: 12pt !important;
     /*
     font-family: arial, sans-serif !important;
     max-width: none !important;
  /*
     position: absolute !important;
     left: 0 !important;
  */
  }

}

@-moz-document domain("gawker.com") {

#header,
#menubar-bg,
.postmeta_permalink_wrapper
{
display:  none  !important;
}

#wrapper {
margin-left:160px;
}

#container  {
background-image:none  !important;
}

}


@-moz-document domain("independent.co.uk") {

#root #top,
#header,
#navigation
{
display:  none  !important;
}

#mainColumn {
  width:  auto !important;
}

#content  {
background-image:none  !important;
}

}


@-moz-document domain("blogs.laweekly.com") {

.entryFooter,
.inform,
.tagsBadges,
#top_stories_container,
#blog_header,
#nav
  {
    display: none !important;
  }

.content_area
  {
    width:  auto !important;
  }

.backbox 
  {
    overflow-x:auto;
    overflow-y:hidden;
  }

}

@-moz-document domain("more.com") {

.slide[style*="display: none;"]
  {
    display:  block !important;
  }

#carousel_header, 
#horizontal_carousel,
.component a:only-child,
#pagination_wrapper div.link /*,
.slide .content .text hr*/
  {
    display:  none !important;
  }

}

@-moz-document domain("web.mit.edu") {

#topbar,
#header,
div#scol1,
div#scol3,
.leadwithimage
  {
    display:  none !important;
  }
div#sheadcol, div#sheadcol *, div#scol2, div#scol2 *
  {
    width:  auto !important;
    color:  #aaa !important;
  }
body, #wrapper
  {
    background:  black !important;
  }
}

@-moz-document domain("more.com") {
.header_main,
#body div#column_6,
.other_related,
.comments_header,
.latest_comments,
.actions,
.liked_it_info
  {
    display: none !important;
  }

#body #column_5
  {
    min-height: 0px !important;
    height: auto !important;
    width: auto !important;
  }
}

@-moz-document domain("newsweek.com") {

#head,
#header,
#mesothorax,
#ToolBox-top,
#ToolBox-bottom,
#comment-box,
#nw-comments,
.inline-promo-link,
.box .top,
.box2 .top,
.box .bot,
.box2 .bot
  {
    display:  none !important;
  }

#metathorax
  {
    width:  auto !important;
  }
body
  {
    background:  #dfd0d0;
    background-image:  none !important;
    padding:  10px !important;
  }

.stage
  {
    padding-bottom:  0px !important;
  }

body #skeleton,
p,
h1, h2, h3, h4, h5, h6,
li
  {
    background: none !important;
/*
    color: #000 !important;
*/
  }

div 
  {
    border:  none !important;
  }

}

@-moz-document domain("techrepublic.com.com") {
#eyebrow,
#masthead,
#headerNav,
#topHTML,
#blogHeader,
.interact,
#rightCol, div#leftCol .post h2:only-of-type, 
div#leftCol .post table:only-of-type,
.cmntBox,
#content ul.navigation,
div#leftCol .post div h3:last-of-type,
div#leftCol .post div p:last-of-type,
iframe,
#preFooter,
#myAvatar
  {
    display: none !important;
  }
  #leftCol
  {
    width: 100% !important;
  }
#width
  {
    float: none !important;
    width: auto !important;
    padding: 28px 10% 0px 10% !important;

  }

}

@-moz-document domain("bbc.co.uk") {
  td.storybody,
  table.datetools,
  table.storycontent
  {
    width:auto !important;
  }

  body
  {
    background:#000 !important;
  }

  table.storycontent tbody tr td div.o,
  .servicev4,
  .ifs_services,
  #blq-foot,
  .newsbanner,
  td.sidebar1,
  .mvtb,
  #blq-acc,
  #blq-mast,
  .storyextra
  {
    display:  none !important;
  }
  #blq-container-inner
  {
    padding-top: 10px !important;
  }
  
}

@-moz-document domain("uk.reuters.com") {
.header,
.searchbar,
#crumbsBand,
.secondaryContent,
.leftrail,
#trackingEnabledModule,
span[name="trackingEnabledModule"]
  {
    display:  none !important;
  }

.primaryContent
  {
    width:  100% !important;
  }
  
div#resizeableText
  {
    float: left;
    font-size: 17px !important;
  }
.content
  {
    margin-top:10px !important;
  }

}

@-moz-document domain("news.yahoo.com") {

#hd, .ft
  { 
    display:  none !important;
  }

.yui-b
  {
    margin-right: 20px !important;
  }

}
