css:
@-moz-document regexp("^(?!chrome-extension:).+") {
*[lang*="ja"],
head[lang*="ja"] ~ body,
*:lang(ja)
{
    letter-spacing: 0.3px !important;
    word-spacing: 0.3px !important;
    font-feature-settings: "pkna" 1 !important;
}

*[lang*="ja"],
head[lang*="ja"] ~ body,
*:lang(ja):not(i):not([class*="fa-"]):not([class*="icon"]):not([aria-hidden="true"]):not(button):not(submit):not(input):not(a):not(li)
{
    line-height: 1.9em !important;
}

i, *[class*="fa-"], *[class*="icon"], *[aria-hidden="true"], button, submit, input, a, li, a *, li *
{
    line-height: unset !important;
}
}
