css:
@-moz-document url-prefix("https://trello.com") {
#board {
  overflow: initial;
}
.list-wrapper:first-child {
  margin-left: 0;
}
.list-wrapper {
  box-sizing: border-box;
  height: auto;
  margin: 0 4px;
  margin-left: 0;
  vertical-align: top;
  white-space: nowrap;
  width: 100%;
  display: block;
}
.list-header {
  background: none;
}
.list {
  background: none;
}
.list-header-name {
  font-size: 20px;
  font-weight: 100;
  color: white;
}
.list-cards {
  overflow: hidden;
}
.list-card {
	display: inline-block;
	margin: 4px;
  padding: 4px;
  vertical-align: top;
  height: 100px;
}
.list-card.hide {
  display: none !important;
}
.list-card-details {
  padding: 0;
  height: 100%;
}
.list-card-details .badges {
  position: absolute;
  bottom: 0;
}
.list-card-labels .card-label {
  background: none;
}
.card-label.mod-card-front {
  min-width: 10px;
  padding: 0 !important;
}
.full-cover-list-card {
  width: 100%;
}
.list-card-labels .card-label-green .label-text {
  color: green;
}
.list-card-labels .card-label-yellow .label-text {
  color: #ffc106;
}
.list-card-labels .card-label-orange .label-text {
  color: orange;
}
.list-card-labels .card-label-red .label-text {
  color: red;
}
.list-card-labels .card-label-blue .label-text {
  color: blue;
}
.list-card-labels .card-label-purple .label-text {
  color: purple;
}
.list-card-labels .card-label-black .label-text {
  color: black;
}
.icon-description, .js-checkitems-badge {
/*   display: none; */
}
.list-header-num-cards {
  color: white;
}
.card-composer-container {
/*   border: 1px solid; */
  width: 200px;
}
}
