css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("deviantart.com") {

.group-roster table.f tbody tr td.f table.f tbody tr td.f div {
	background:#DFE6DE !important;
	}
#gmi-MembershipGlobalAccessPanel {
	padding:20px 20px 50px 20px !important;
	background:#DFE6DE url(http://e.deviantart.net/emoticons/e/eager.gif) center 30px no-repeat!important;
	}
.roster table.gruze tbody tr td.gruze-main .iconset-gruser {
	background:#DFE6DE !important;
	}
#gmi-RosterSidebarEntry {
	background:#C5D3C2!important;
	}
#gmi-RosterSidebarEntry:hover{
	background-color:#CCD9C8!important;
	}
#gmi-GMRoleEditor {
	border:1px solid #A6B2A6 !important;
	margin:0 0 20px 0 !important;
	background:#C5D3C3!important;
	-moz-border-radius:20px 20px 6px 6px;
	position:relative!important;
	padding-bottom:20px!important;
	}
.cruise-boxes strong i.icon{
	margin-top: 1px !important;
	}
.cruise-boxes strong {
	margin:-8px -8px 10px -8px !important;
	background:#E0BB0B url(http://st.deviantart.com/minish/gruzecontrol/stfo.gif) repeat-x!important;
	padding:5px 0 5px 6px!important;
	font-size:14pt;
	border:1px solid #F7D948!important;
	outline:1px solid #D4B10A!important;
	border-bottom:0px solid #868F86!important;
	-moz-border-radius:6px 6px 0 0;
	-moz-outline-radius:6px 6px 0 0;
	font-family: Trebuchet MS,sans-serif;
	letter-spacing: -1px;
	font-weight: normal;
	color:#2C3635!important;
	text-shadow: 1px 1px 0 #FFF1AF;
	}
.cruise-boxes a.a {
	display:inline-block!important;
	margin:10px 0 0 0 !important;
	-moz-border-radius:6px;
	text-decoration:none !important;
	background:url(http://st.deviantart.com/minish/messages/gmbutton2.gif) -1px -1px no-repeat !important;	
	right:8px;
	height:25px;
	line-height:24px;
	width:130px;
	text-align:center;
	float:right !important;
	border:1px solid #B3BFB3!important;
	-moz-border-radius:6px;
	}
.cruise-boxes a.a:hover {
	background:url(http://st.deviantart.com/minish/messages/gmbutton2.gif) -1px -28px no-repeat !important;	
	}
.cruise-boxes a.a:active,
.cruise-boxes a.a:focus {
	background:url(http://st.deviantart.com/minish/messages/gmbutton2.gif) -1px -55px no-repeat !important;	
	}
.cruise-boxes dd.f a.a {
	position:absolute!important;
	color:#6F7F7C!important;
	display:inline!important;
	margin:0px 30px 0 0!important;
	padding:1px 0 0 22px !important;
	background:url(http://i.deviantart.net/icons/notes/folder-closed.gif) no-repeat !important;
	text-align:left;
	width:auto;
	height:auto;
	right:auto;
	line-height:normal;
	left:1px;
	border:0!important;
	-moz-border-radius:0;
	}
.cruise-boxes dd.f a.a:hover{
	color:#4D5F5C!important;
	text-decoration:underline!important;
	}
.cruise-boxes dd.f {
	font-weight: bold!important;
	}
#gmi-GMRoleEditor.cruise-boxes div {
	background:#C5D3C3!important;
	}
#gmi-GMRoleEditor.cruise-boxes .bpp-vote-details {
	background:#D3DFD1 url(http://sh.deviantart.com/shadow/alpha-000000/9-0.50/2000/50/null.png) -100px -65px no-repeat!important;
	padding:10px!important;
	margin:0px 0px 5px -6px!important;
	-moz-border-radius:0 0 6px 6px;
	border:1px solid #A6B2A6!important;
	}
.cruise-boxes dd.f label {
	background:#b2c2b5!important;
	margin:10px 0 0 -6px!important;
	display:block!important;
	-moz-border-radius:6px;
	padding:3px 0 3px 8px!important;
	color:#555a56!important;
	font-size:10pt;
	border:1px solid #a6b7aa;
	}
.cruise-boxes dd.f input{
	vertical-align:-15%;
	}
.bpp-vote-details,
.cruise-boxes dd.f a.a,
.cruise-boxes dd.f label {
	font-weight: normal!important;
	}
#gmi-GMRoleEditor.cruise-boxes #gmi-GMActionEditor {
	background:#9EB1A2!important;
	margin:10px 10px 0 0!important;
	}
#gmi-BPPDropDown .bpp_invite_to_roles #gmi-GMActionEditor span.gmaction_role {
	color:#9EB1A2!important;
	}
#gmi-GMRoleEditor.cruise-boxes .bpp_invite_to_roles {
	margin:10px 0 0 -6px!important;
	-moz-border-radius:6px;
	background:#9EB1A2!important;
	padding:8px 0 8px 8px!important;
	}
.cruise-boxes a:focus {
	outline:0!important;
	}
#gmi-Gruser #group #gmi-GPage #gmi-AdminArea td.gruze-main .selected .selinherit,
#gmi-Gruser #group #gmi-GPage #gmi-AdminArea td.gruze-main .selected a,
#gmi-Gruser #group #gmi-GPage #gmi-AdminArea td.gruze-main .selected {
	background-color:#498091!important;
	}
#gmi-Gruser #group #gmi-GPage #gmi-AdminArea td.gruze-main .selected:hover,
#gmi-Gruser #group #gmi-GPage #gmi-AdminArea td.gruze-main .selected:hover a,
#gmi-Gruser #group #gmi-GPage #gmi-AdminArea td.gruze-main .selected:hover span.selinherit {
	background-color:#4F8C9F!important;
	}
#gmi-Gruser #group #gmi-GPage #gmi-AdminArea td.gruze-sidebar .selected .selinherit,
#gmi-Gruser #group #gmi-GPage #gmi-AdminArea td.gruze-sidebar .selected a,
#output #gmi-Gruser #group #gmi-GPage #gmi-AdminArea td.gruze-sidebar .selected {
	background-color:transparent!important;
	}
#gmi-GPage .roster .gme-member {
	cursor:default;
	border-bottom:1px dashed #9EB1A2!important;
	}
#gmi-GPage .roster .gme-member:hover{
	background:#E9EFE8!important;
	}
#gmi-EditableMember span {
	padding-bottom:2px;
	}
#gmi-EditableMember span span span {
	background-position: 0 -30px!important;
	}
span.gghonk {
	color:#777F79!important;
	background:none!important;
	}
span#gmi-RosterInvite {
	border-bottom:1px solid #9EB1A2!important;
	background:#C5D3C2;
	text-align:center;
	}
span#gmi-RosterInvite a{
	background:url(http://st.deviantart.com/minish/messages/gmbutton2.gif) -1px -1px no-repeat!important;
	height:25px!important;
	line-height:24px!important;
	display:inline-block!important;
	padding:0 10px!important;
	text-decoration:none !important;
	-moz-border-radius:6px;
	border:1px solid #AFBEB0!important;
	}
span#gmi-RosterInvite a:hover{
	background-position:-1px -28px!important;
	}
span#gmi-RosterInvite a:focus{
	background-position:-1px -55px!important;
	}
span#gmi-RosterInvite img{
	display:none!important
	}

}
