/* ==UserStyle==
@name         Pinboard x Nord
@version      20220424.10.40
@namespace    https://github.com/oldtowneast
@description  A Stylus theme for Pinboard.in inspired by Pinboard Dark by Scott Killen and Nord.
@author       oldtowneast
@license      WTFPL License
==/UserStyle== */

@-moz-document domain(pinboard.in), url-prefix(http://pinboard.in)
{

* {
    /* font-family: Avenir Next, Helvetica Neue, Arial, Sans; */
    font-family: Inconsolata, Hasklig, "M + 2m", Sans;
    font-family: "Fira Sans", Sans;

--nord00: #2E3440;
--nord01: #3B4252;
--nord02: #434C5E;
--nord03: #4C566A;
--nord04: #D8DEE9;
--nord05: #E5E9F0;
--nord06: #ECEFF4;
--nord07: #8FBCBB;
--nord08: #88C0D0;
--nord09: #81A1C1;
--nord10: #5E81AC;
--nord11: #BF616A;
--nord12: #D08770;
--nord13: #EBCB8B;
--nord14: #A3BE8C;
--nord15: #B48EAD;

}

div#logo {
    font-size: 18px !important;
}

div#logo img.pin_logo {
    content: url("https://files.catbox.moe/1hms50.png") !important;
    width: 16px;
    height: 16px;
}

body {
    background-color: #2E3440;
    color: var(--nord04);
}

.display {
    line-height: 1.5em !important;
}

#banner,
.user_navbar,
#tag_cloud {
    font-size: 115% !important;
}

#banner {
    background: transparent;
}

.bookmark_title {
    font-size: 1.6em !important;
    color: #4183c4 !important;
}

div.display a {
    color: #4183c4;
}

a.banner_username,
div#main_column p b a,
div#main_column p a,
div#top_menu a,
div#footer a,
a.url_display:hover,
a.edit:hover,
a.delete:hover,
div#title a,
div.service_box a {
    color: #4183c4 !important;
}

div#footer hr {
    border-top-color: #484848 !important;
}

div.small_username b a,
div#main_column b a,
a#login {
    color: #B48EAD !important;
}

.private {
    margin-top: 15px !important;
}

a {
    color: var(--nord15);
}



a.url_display {
    font-size: 1.2em !important;
}

a.edit {
    color: #EBCB8B !important;

}

a.delete {
    color: #BF616A !important;
}

a.url_link {
    padding: 1px 2px !important;
    font-weight: 300 !important;
}

div.search_button p label {
    color: #888 !important;
}

a.bundle {
    color: #A3BE8C;
    font-weight: bold;
}

a.bundle {
    color: #A3BE8C;
    font-weight: bold;
}

.private { /* Tyler was here */
    background: #a5aab4;
    border: 1px solid #6f7787;

}

#tag_cloud a.tag_heading_selected {
    font-weight: bold;
    color: #D8DEE9;
}

.user_navbar a:hover {
    color: #5E81AC;
}

.bookmark_title {
    font-size: 1.6em !important;
    color: #81A1C1 !important;
}

a.banner_username,
div#main_column p b a,
div#main_column p a,
div#top_menu a,
div#footer a,
a.url_display:hover,
a.edit:hover,
a.delete:hover,
div#title a,
div.service_box a {
    color: #5E81AC !important;
}

:root {
    --logo-color: #88C0D0;
    --white: #ECEFF4;
}

#search_query_field {
    background: #D8DEE9 !important;
    border-color: #E5E9F0;
}


.private {
    background: #434C5E;
    border: 1px solid #4C566A;
}

#edit_bookmark_form input {
    font-size: 90%;
    border: 1px solid #E5E9F0;
    color: #434C5E;
    margin-bottom: 3px;
}

#edit_bookmark_form {
    color: #434C5E;
    visibility: hidden;
}

a.navlink {
    text-decoration: none;
}

a {
    text-decoration: none;
}


/* 
#edit_bookmark_form textarea {
    font-size: 90%;
    border: var(--nord04) 1px solid;
    margin-bottom: 3px;
    background: var(--nord01);
}


.edit_form_input {
    font-size: 90%;
    background: var(--nord03);
    color: var(--nord05);
}

#edit_bookmark_form_url_field {
    background: var(--nord03);
    color: var(--nord05);
}
*/

/* Tyler was here */

#edit_bookmark_form input {
    font-size: 1.10em;
    border: var(--nord05) 1px solid;
    color: var(--nord05);
	margin-bottom: 3px;
}


.text {
    color: white !important;
}

#bulk_edit {
    color: #EBCB8B;
}

#url {
    background: var(--nord01);
    border: 1px solid var(--nord03);
    color: var(--nord06);
}

#tags {
    background: var(--nord01);
    border: 1px solid var(--nord03);
    color: var(--nord06);
}

.textarea {
background: var(--nord01);
border: 1px solid var(--nord03);
color: var(--nord06);
}

#description {
background: var(--nord01);
border: 1px solid var(--nord03);
color: var(--nord06);

}

/* tbody,
form,
tr,
table,
td, */
input,
textarea {
background : var(--nord01);
border : 1px solid var(--nord03);
color : var(--nord06);
font-size: 1.15em;

}

input[type=submit] {
    background: linear-gradient(#5E81AC, #81A1C1) !important;
    border-color: #083 !important;
    position: relative !important;
    display: inline-block !important;
    padding: 2px 3px !important;
    font-size: 13px !important;
    font-weight: 600 !important;
    line-height: 20px !important;
    white-space: nowrap !important;
    vertical-align: middle !important;
    cursor: pointer !important;
    user-select: none !important;
    border: 1px solid rgba(27, 31, 35, 0.2) !important;
    border-radius: 0.25em !important;
    -webkit-appearance: none !important;
    color: #e2e2e2 !important;
}

input[type=submit]:hover {
    background: linear-gradient(#5E81AC, #81A1C1) !important;
}

a.tag {
color: #8FBCBB !important;
}

a.tag.selected {
    background-color: #EBCB8B !important;
}

.description blockquote {
    color: #E5E9F0 !important;
}

.description,
div#main_column p b,
div#main_column b,
div#profile_main_column,
td {
    margin: 5px 0 !important;
    font-size: 1.2em !important;
    line-height: 1.4em !important;
    color: #D8DEE9 !important;
    font-weight: 400 !important;
}

.when,
.edit_links {
    margin: 5px 0 !important;
    font-size: 1.1em !important;
    line-height: 1.4em !important;
    color: #D8DEE9 !important;
    font-weight: 300 !important;
}

.star,
a.cached {
    font-size: 200% !important;
}

.star,
.cached,
.source {
    display: none !important;
}

.url_link {
    background-color: #f1daad !important;
    border: 1px solid #EBCB8B !important;
    font-size: 1.1em !important;
    line-height: 1.4em !important;
    font-weight: 300 !important;
}

a.tag {
    font-weight: 500 !important;
    font-size: 1.115em !important;
}

.description,
div#main_column p b,
div#main_column b,
div#profile_main_column,
td {
    color: #E5E9F0 !important;
    font-weight: 400 !important;
}

#search_query_field {
    background: var(--nord05) !important;
    border-color: var(--nord06);
    color: var(--nord03);
}

element {
	display: inline;
    color: var(--nord01);
}

input.submit {
    color: var(--nord01);
}

#edit_form_checkboxes {
    color: var(--nord05);
}

/* NOTES!!!

div.search_button p label

color: var(--nord06);
font-family: var(--font-family-empty);
*/

/* thanks buddy
https://github.com/brendanmurty/pinboard-user-style/blob/master/pinboard.css 

*/
input[type="button"],
input[type="submit"],
input[type="reset"] {
    background-color: var(--nord08) !important;
    color: var(--nord005) !important;
}
}
