css:
@namespace url(http://www.w3.org/1999/xhtml);


/* IMBD Grey'n'Blue by KonphliKt v. 0.1 */

@-moz-document domain("imdb.com") {

/* Slightly Stretch Left Sidebar */
#tn15lhs {
width: 140px !important;
background-color: #121212 !important;
margin-left: -20px !important;

}

/* Wider IMDB - Can Change to Fit Your Needs */
#root,
#navbar {
width: 1280px !important;
}

/* Get Rid of Action Box and some White Space */
.action-box,
#tn15bot .right {
display: none !important;
}

/* Change Some Colors to Grey */
#pagecontent,
#tn15lhs .photo,
#root,
td.lhscol,
.hpRightNav,
div.tvgrid,
.tvgrid .tvlistings div,
#navbar ul.main_nav li ul.sub_nav li a {
background-color: #292929 !important;
}

/*Blue Border Around Sub Nav Menu */
#navbar ul.main_nav li ul.sub_nav {
background: #008cff !important;
}

/* Actor / Character Area Color Changes */
#tn15main table.cast td {
border-bottom: 1px solid #008cff !important;
border-top: 1px solid #008cff !important;
}

/* Other Changes from Dotted to Solid Borders */
.lhscol ol, 
.lhscol ul, 
.lhscol .boxBottom {
border-bottom: 1px solid #008cff !important;
}

/* Change Background Color of News Widget & Change NavBar to Match Root */
.newsWidget ul.newsTabs a,
#navbar {
background: #292929 !important;
}

.newsWidget ul.newsTabs a:hover {
background: #141414 !important;
}

/* Button Tweaking on "Was the above useful to you?" */
/*button.secondary, .linkasbutton-secondary {
background: #292929 !important;
color: #008cff !important;
border: none !important;
}*/

/*button.secondary:hover, .linkasbutton-secondary:hover {
background: #292929 !important;
color: #008cff !important;
border: none !important;
}*/


/* More Button Tweaking */
button.secondary:hover, button.secondary-hover, .linkasbutton-secondary:hover {
border:none !important;
color:#008cff !important;
background: #292929 !important;
}

button.secondary, .linkasbutton-secondary {
background: #008cff !important;
border: none !important;
color:#eee !important;
font-size:10px;
}


/* More Color Changes as You Delve into IMDB */
#styleguide-v2, 
#styleguide-v2 #wrapper,
#tn15main table.boards td,
#tn15main .strip,
#tn15main table.cast td,
#footer,
td,
#navbar ul.main_nav li a:hover,
.tvgrid .tvlistings div:hover {
background: #141414 !important;
}

/* To Keep Menu From Changing to White Background When Choosing Sub Nav */
a.nb_link {
background: #292929 !important;
/*-moz-border-radius-bottomleft: 6px !important;
-moz-border-radius-bottomright: 6px !important;*/
-moz-border-radius: 6px !important;
padding: 5px !important;
}

/* Bring NavBar up So Bottom Edges Don't Bleed */
#navbar ul.main_nav {
bottom: 2px !important;
}

/* Dotted Borders to Solid 1px Blue Border */
#tn15main,
#tn15main hr,
#tn15main .strip,
.swiki_intro .box {
border: 1px solid #008cff !important;
}

#tn15bot {
border-top: 1px solid #008cff !important;
}

#tn15lhs h6 {
border-style: solid !important;
}

/* From Black to Near White Font */
a:link, 
a, 
#tn15main div.info-content, 
#tn15main .info h5, 
#tn15main .info-offset h5, 
#tn15main, #tn15main table, 
#tn15 .headerinline h3,
#tn15lhs h6,
#tn15main h3,
#footer p,
#tn15main table.recs td small,
#tn15main table.cast td,
#tn15crumbs,
#root,
#root td {
color: #eee !important;
}

/* Selected Menu Highlight for Left Sidebar */
#tn15lhs a.link.selected {
background: #292929 !important;
}

/* Change Link Hover Color */
a:hover {
color: #008cff !important;
text-decoration: none !important;
}

/* Remove Underlines from Links */
a:link {
text-decoration: none !important;
}

/* Change Colors of Headings to Blue */
#content-2-wide #sidebar h3,
td.lhscol h2, 
.hpRightNav h2,
#tn15lhs h6,
#tn15main h3,
#tn15 .headerinline h3,
#tn15title h1,
#tn15 h1, 
#tn15 h2, 
#tn15 h3, 
#tn15 h4, 
#tn15 select, 
#tn15 .small {
color: #008cff !important;
}

/* Change TV Grid Colors and Backgrounds */
.tvgrid .tvshow {
color: #eee !important;
}

td.lhscol {
border-top: none !important;
}

/* Give the Images a Slight Shadow */
img:hover,
#tn15title h1:hover,
#tn15main table.cast:hover {
-moz-box-shadow: 0 2px 16px rgba(254,254,254,0.625) !important;
}

/* Sidebar Color Change */
.aux-content-widget-2 {
background-color: #292929 !important;
}

/* Colors for other Links in IMDB */
.yn /* yes / no buttons */ {
color: #008cff !important;
background-color: #141414 !important;
}

/* Message Board Header */
#tn15main table.boards th.left,
#tn15main table.boards th.right {
background: #008cff !important;
border: 1px solid #000 !important;
}

/* Misc Changes */
.chart_head {
background: #008cff !important;
color: #141414 !important;
border: 1px solid #141414 !important;
}

#styleguide-v2 #sidebar-narrow {
border: none !important;
}

.tabular td.item:hover {
background: #292929 !important;
border: 1px solid #008cff !important;
}

.tabular td.item {
border: 1px solid #008cff !important;
}

/* Create Shadow Around Links When Hovered - Comment to See - W.I.P. */
/*a:hover {
-moz-box-shadow: 0 2px 16px 2px rgba(254,254,254,0.625) !important;
-moz-border-radius: 4px !important;
}*/

div {
color: #999 !important;
}

/* Message Board Area Changes */
font {
color: #008cff !important;
}

blockquote {
color: #008cff !important;
background: #292929 !important;
-moz-border-radius: 4px !important;
}

/*a:visited {
font-style: italic !important;
text-decoration: none !important;
color: #008cff !important;
}*/

/* Remove Dotted Line Around Links When Clicking Them */
a {
outline: none !important;
}

/* 1/1/10 */
/* Picture Canvas Was White - Now Grey - Update 1/1/10 */
#media_canvas {
background: #292929 !important;
border: 1px solid #008cff !important;
}

/* Updates to Actor/Actress Photo Area */
div#photo-links {
background: #090909 !important;
}

div#meta {
background: #191919 !important;
}

.btn {
background-image: none !important;
background: #393939 !important;
}

.btn:hover {
background: #090909 !important;
border-color: #008cff !important;
}

div#photo-caption {
background-color: #090909 !important;
}

div#photo-container {
background-color: #191919 !important;
}

div#thumbnail-gallery {
background: #008cff !important;
border-top: 1px solid #191919 !important;
}

.aux-content-widget-2 {
-moz-border-radius: 6px !important;
}

/* TV Listing Fix on Front Page */
div.tv_logo {
background: #292929 !important;
}

#homepage_tv_snapshot .tv_show {
background: #191919 !important;
}

#homepage_tv_snapshot .tv_show:hover {
background: #090909 !important;
}

#homepage_tv_snapshot .tv_navigation {
background: #008cff !important;
}

.tv_time span {
color: #111 !important;
}
}
