css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("userscripts.org") {

#userbox img.photo {
top: -1px !important;
-moz-box-shadow: 0px 1px 8px rgba(255, 255, 255, 0.5) !important;
}

#userbox img.photo:hover { -moz-box-shadow: 0px 1px 8px rgba(255, 255, 255, 1.0) !important; }

p.notice {
-moz-border-radius: 5px !important;
}

th.la, th.tiny { border: 0 !important; }

body#forums-show th.la, body#users-index th.la { border: 1px solid #000 !important; -moz-border-radius: 0 !important; }

th.la a:visited, th.la a:hover { color: #fff !important; }

th.la:first-child { -moz-border-radius: 8px 0 0 0 !important; }

th.la:last-child { -moz-border-radius: 0 8px 0 0 !important; }

td.script-meat, table.wide.forums td.vat, table.wide.forums td.c2, tr.guide td:first-child, tr.group td:first-child { background: #eee !important; }

#browser-news .webkit {
background: url(data:image/png;base64,) no-repeat !important;
}

#browser-news .greasemonkey {
background: url(data:image/png;base64,) no-repeat !important;
}

#navbox {
border-top: 0px solid #000 !important;
border-bottom: 0px solid #00ffff !important;
}

#nav ul#mainmenu li a {
border: 1px solid #ff6317 !important;
margin-right: 1px !important;
background: url('data:image/png;base64,') repeat-x !important;
text-shadow: 1px 1px 0px #5f5f5f !important;
-moz-box-shadow: 0px 1px 0px #fff !important;
}

#nav ul#mainmenu li a:hover {
border: 1px solid #ff6000 !important;
background: url('data:image/png;base64,') repeat-x !important;
}

form#script_search { margin-top: -6.5px !important; }

input#script_q {
-moz-border-radius: 10px !important;
text-align: center !important;
padding: 1.5px !important;
}

input#script_q:focus {
-moz-box-shadow: 0px 2px 0px rgba(127, 127, 127, 0.5) !important;
}

ul#homeMenu {
-moz-border-radius: 4px 4px 4px 4px !important;
margin-top: -2px !important;
border: 2px solid #ff9900 !important;
background: #000000 !important;
padding: 0 !important;
width: 140px !important;
right: -15px !important;
}

ul#homeMenu li { 
padding: 2px 10px !important;
}

ul#homeMenu li:hover {
display: block !important;
background: rgba(255, 255, 255, 0.2) !important;
}

body#jetpacks-index #content li {
margin-bottom: 5px !important;
background: #eeeeee !important;
border:1px solid #DDDDDD !important;
list-style-type: none !important;
padding: 5px !important;
-moz-border-radius: 4px !important;
}

body#jetpacks-index #content li:hover {
background: #dfdfdf !important;
}

body#jetpacks-index #content li a {
display: block !important;
margin-bottom: -15px !important;
}

body#jetpacks-index #content li span a { margin-bottom: 0px !important; }

body#forums-index .stats {
border: 1px solid #339933 !important;
-moz-border-radius: 10px !important;
}

body#articles-index #content h1 { margin-bottom: 15px !important; }

body#articles-index #content h2 { font-size: 2em !important; }

/* ### Remove code if you want to see Uservoice button on frontpage ### */

a#uservoice-feedback-tab { display: none !important; }
input#q {
-moz-border-radius: 10px !important;
text-align: center !important;
padding: 1.5px !important;
}

input#q:focus {
-moz-box-shadow: 0px 2px 0px rgba(127, 127, 127, 0.5) !important;
}


}
