@-moz-document domain("comm100.io") {
/* How to change colors:
  right click thing you want to change in browser, click Inspect
  in popup Devtools window, hover over div tags in Elements panel to see what highlights in com100
  copy div tag from Styles panel on the right, paste below, add color
*/

:root {
  --main-bg-color: #2e2e2e;
  --lessdark-bg-color: #aeaeae;
  --comm100-blue-bg: #CCE7F5;
  --comm100-yellow-bg: #ffff99;
  --text-dark: #373d3f
}

/* top bar */
.Header__headSection--2_twk {
    background-color: var(--main-bg-color) !important;
}

/* channel bar, far left */
.AgentTab__agenttabwraper--2CQcG {
    background-color: var(--main-bg-color) !important;
}

/* left menu bg */
.menu__filterMain--20ul7 {
    background-color: var(--main-bg-color) !important;
}

/* ticket list */
.list__listContainer--pXeXx  {
    background-color: var(--main-bg-color) !important;
}

/* ticket header */
.ticketContent__headerUl--2vXAD {
    background-color: var(--lessdark-bg-color) !important;
}

/* message bg */
.Resizer__mainContainer--2rRfb {
    background-color: var(--main-bg-color) !important;
    color: var(--text-dark) !important;
}

/* info and tags */
.ticketInfo__infoContainer--2qGC2{
    background-color: var(--main-bg-color) !important;
}

/* ticket number */
.label__fitcontent--PZVk8 {
    background-color: var(--lessdark-bg-color) !important;
}

/* ticket contact */
.ticketInfo__contactIdentities--AxyqM {
    background-color: var(--lessdark-bg-color) !important;
}
}
