css:
@-moz-document domain("anydice.com")
{
:root {
    --scroll: url("https://i.imgur.com/XJY6fa1.jpg");
    --baverage: #F2ECE4;
    --h1color:#58180D;
    --tdcolor:#E0E5C1;
    --titlecolor:#ED1C24;
    --underlinecolor:#f0bf48;
    --statblocklines:#9c2b1b;
    --redscroll:url(https://i.imgur.com/YvYGqOX.jpg);
    --bluescroll:url(https://i.imgur.com/w2m4BgW.jpg);
    --seascroll:url(https://i.imgur.com/dKciX3y.jpg);
    --placeholdercolor:#6b6b6b;
}


body
  {
	background-image: var(--scroll);
  }
*::selection{
	background:var(--h1color)!important;
    color:#fff;
}
#codeInput, #linkField, #exportDisplay textarea, #rollerOutput, #rollerAmount  {
	background-color:var(--baverage);
}
#codeInput {
    font-size:1.3em;
    font-weight:900;
    border-radius:10px;
}

#codeInput, #linkField{
	width:98% !important;
}
#tableDisplay table,#exportDisplay textarea {
	width:99% !important;
}
#exportDisplay textarea {
	border-radius:10px;
}
table td:nth-child(3) {
	background-color: #F2ECE4;
    box-shadow:inset 0px 0px 0px 1px #e4dbca;
}
table caption, table tr,#exportDisplay textarea{
	font-size:1.2em !important;
}
#sidebar, #tableDisplay div {
	background-color:var(--h1color);
}
.option .selector, #menu .selector {
	background-color:var(--tdcolor);
    color:#000;
}
.option .selected,#calculateButton {
    background-color:var(--h1color);
    color: #ffc;
}
.option .selected,#calculateButton,.option .selector, .option h3 {
    font-size:1.2em;
}
.option h3 {
	font-weight:900;
}

#menu .selector.selected, #calculateButton:hover,.option .selected:hover {
	background-color:var(--statblocklines);
    color:#fff;
}
#menu .selector.selected:hover {
	background-color:var(--titlecolor);
    color: #fff;
}
#menu .selector:not(.selected):hover,.option .selector:not(.selected):hover {
 	background-color:var(--underlinecolor) !important;
    color:#000;
}

#sidebar #footer:before {
	display:block;
    content:"";
    background-image:url(https://raw.githack.com/5e-Cleric/Images/main/My-logo.svg);
    background-size:100%;
    background-repeat:no-repeat;
    position:absolute;
    left:47.5px;
    bottom:60px;
    width:120px;
    height:120px;
    filter:invert(100%);
    transition-duration:1s;
    
}
#sidebar #footer:after {
	display:block;
    content:"custom style made by u/5e_Cleric";
    color:white;
    position:absolute;
    left:47.5px;
    bottom:30px;
    width:120px;
    height:20px;
    transition-duration:1s;
    transition-delay:.7s;
    opacity:0;
    font-weight:800;
}
#sidebar #footer:hover:before {
    transform:rotate(360deg);
}
#sidebar #footer:hover:after {
	opacity:1;
}
.text.page {
	background-image:var(--scroll);
}
.text.page h2:hover{
	color:#9c2b1b !important;
}
a {
color:#9c2b1b;
}




}
