css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("http://YOURDOMAIN.com:2095/"), 
url-prefix("http://YOURDOMAIN.com:2095/") {


/* Plasticmind says: Override The Horde Ugliness! 
   Jesse Gardner, http://plasticmind.com
------------------------------------------------- */

body,
body table,
body tr,
body td,
body a,
body li {
font: bold 12px/1.5 Helvetica !important;
}

#menu {
background: #eee !important;
height: 20px !important;
overflow: hidden !important;
border: none !important;
margin: 0 !important;
padding: 0 !important;
display: none;
}
#menu a {
font: bold 10px/1.5 Helvetica !important;
color: #555 !important;
}
#menu li a {
background: none !important;
border: none !important;
height: auto !important;
padding: 0 6px !important;
margin: 0 !important;
}
#menu li a.current {
background: #ccc !important;
}
#menu li img {
display: none !important;
}
#menu select {
font: normal 12px/1.5 Helvetica !important;
padding: 4px 1px 1px 1px !important;
border: solid 1px #ccc !important;
-moz-border-radius: 4px !important;
margin: 16px 6px 0 0 !important;
color: #666 !important;
}
.control td {
font: normal 13px/1.5 Helvetica;
color: #555 !important;
padding: 1px 5px !important;
}
.control td a {
color: #333 !important;
}
.control th {
padding: 2px 0 0 5px !important;
font: bold 10px/1 Helvetica !important;
color: #aaa !important;
}
.control th a {
font: bold 10px/1 Helvetica !important;
color: #aaa !important;
}
.control .unseen td a {
color: #111 !important;
}
.control .seen td a {
font-weight: normal !important;
}
.header {
font: bold 22px/1.5 Helvetica !important;
color: #fff !important;
background: #434851 !important;
border-bottom: solid 1px #eee !important;
padding: 6px 12px 3px 12px !important;
}
.header img {
position: relative;
top: -10px;
}
.control:first-child {
font: bold 12px/1.5 Helvetica !important;
background: #ddd !important;
border-bottom: solid 1px #bbb !important;
}
.control select {
font: normal 12px/1.5 Helvetica !important;
padding: 1px 2px 3px 2px !important;
border: solid 1px #ccc !important;
-moz-border-radius: 4px !important;
margin: 6px 0 0 0 !important;
color: #666 !important;
}
.control:first-child a {
font: normal 12px/1.5 Helvetica !important;
color: #888 !important;
margin: 0 4px;
}
.control:first-child .leftAlign {
color: #ccc !important;
}
/* --- Stylize The Delete Button --- */
.control:first-child .leftAlign a:first-child {
color: #fff !important;
background: #933 !important;
padding: 4px 5px 2px 5px !important;
text-decoration: none !important;
font-weight: bold !important;
-moz-border-radius: 4px !important;
margin-bottom: 12px !important;
-moz-box-shadow: rgba(255, 255, 255, 1) 0px 0px 1px !important; /* FF 3.5+ */
}
/* --- Undo the styling for the Move Button --- */
.control:first-child .leftAlign form a:first-child {
background: none !important;
font-weight: normal !important;
color: #888 !important;
-moz-box-shadow: none !important;
}
.mailboxLegend {
display: none !important;
}

/* Sidebar Stuff */
body {
border-left: solid 1px #eee;
}
body.sidebar {
background: #ddd !important;
border: none;
font: bold 11px/1.5 Helvetica !important;
}
#sidebarPanel {
background: none !important;
border: none !important;
}
#sidebarPanel span,
#sidebarPanel a {
font: bold 11px/1.5 Helvetica !important;
border: none !important;
}
#sidebarPanel span {
color: #000 !important;
}
#sidebarPanel a {
color: #555 !important;
}

/* --- Message Body Styles --- */
#messageBody table td {
color: #111 !important;
padding: 12px 24px 28px 24px!important;
}
#messageBody table .fixed {
  font:13px/1.3 Helvetica !important;
}

}
