css:
@-moz-document 

url-prefix(http://sites.google.com/) {

.sites-embed h4 {
	font-size: 15px !important;
	direction: rtl !important;
	text-align: right !important;
}
.announcement h4 a {
	font-size: 20px !important;
	direction: rtl !important;
	text-align: right !important;
}

#title-crumbs, #title-crumbs a {
	direction: rtl !important;
	text-align: right !important;
}

.sites-attachment-inner ul li {
	background-position: right !important;
	padding-right: 19px !important;
}

#sites-list-controls div, #jot-content, #jot-content table, #jot-content table tr td, #jot-content table tr th, .sites-embed-content, .sites-embed-content table, .sites-embed-content table tr td, .sites-embed-content table tr th, #sites-canvas, #sites-comment-wrapper, #sites-attachment-wrapper, .sites-attachment-inner ul, .sites-attachment-inner ul li, .sites-attachment-inner ul li span, #sites-canvas-main, .sites-embed-content ul, .sites-embed-content ul li, .sites-sidebar-recent-activity ul, .sites-sidebar-recent-activity ul li, .sites-layout-tile span, .sites-tile-name-content-1 span, .sites-sidebar-nav ul, .announcements, .sites-sidebar-nav ul li, #sites-chrome-sidebar-right h4, .announcement, .sites-clear, textarea, input,.modal-dialog-content {
	font-family: tahoma !important;
	direction: rtl !important;
	text-align: right !important;
}

#sites-page-title-header span {
	direction: rtl !important;
	text-align: right !important;
}

.sites-attachment-icon, .sites-comment-icon {
	float: right !important;
}
.postfooter p, .modal-dialog-buttons {
	direction: ltr !important;
      
}


.sites-list-sort-button {
	float: right !important;
}

#sites-chrome-everything, span #sites-page-title, #sites-chrome-everything .goog-tree-item-label {
	font-family: tahoma, arial !important;
}

#sites-chrome-everything h1, #sites-chrome-everything h2, #sites-chrome-everything h3, #sites-chrome-everything h4, #sites-chrome-everything h5, #sites-chrome-everything h6 {
	font-family: Arial !important;
}

textarea {
  height: 200px !important;

}

}
