css:
@-moz-document regexp('https://animecix.net/') {
    media-grid .item .ep_num, media-grid .item .last-days{
        color: white !important;
    }
    :root{
        --be-background: white !important;
        --be-text: black !important;
        --be-accent-default: rgb(0 74 255) !important;
        --be-background-alternative: white !important;
    }
    a:-webkit-any-link {
        color: black;
    }
    .list, .ng-star-inserted #div{
        background-color: var(--be-background) !important;
    }
    .title, .genre, .mat-button-wrapper{
        color: white !important;
        
    }
    material-navbar:not(.login-button-accent) .login-btn{
        background: black !important;
    }
    .footer-content>.container{
        background: white !important;
    }
    .auth-panel[_ngcontent-rhp-c323]{
        background-color: rgb(255 255 255) !important;
    }
}
