css:
/* :::: Rounded Box :::: */
#peopleSearchInput,
#searchInput,
#search-box,
.input-widget,
#searchbox,
#msgSubject,
#searchfield,
.findbar-textbox,
#searchFilter,
#editBMPanel_tagsField,
#editBMPanel_locationField,
#editBMPanel_namePicker,
#editBMPanel_keywordField,
#editBMPanel_feedLocationField,
#editBMPanel_siteLocationField,
#urlbar,
.searchbar-textbox {
 padding-top: 2px !important;
 -moz-appearance: none !important;
 -moz-border-radius: 100% !important;
 background: #FFF !important;
 -moz-box-shadow: 0 1px 1px rgba(0, 0, 0, 0.1), inset 0 1px 1px rgba(0, 0, 0, 0.5) !important;
}

/* :::: Adjustments :::: */
#urlbar,
.searchbar-textbox {
  border-bottom: transparent !important;
}
#identity-box {
  margin-top: -2px !important;
}

/* :::: Find Bar :::: */
.findbar-textbox[status="notfound"] {
 color: black !important;
 -moz-box-shadow: inset 0 0 2px 1px rgba(255, 0, 0, 0.7), 0 0 4px 1px rgb(255, 0, 0), inset 0 1px 2px rgba(0, 0, 0, 0.7) !important;
}
.find-field-container.findbar-find-fast {
 -moz-appearance: none !important; 
 background: none !important;
 border: none !important; 
 margin: 0 !important;
}

/* :::: Search Bar :::: */
.searchbar-engine-button {
 background: transparent !important;
 -moz-box-shadow: none !important;
 -moz-border-radius: 10px 0 0 11px !important;
}
#searchbar { padding-bottom: 1px !important; }
#searchbar .autocomplete-textbox-container { -moz-border-radius: 12px !important; }
.searchbar-engine-button .box-inherit.button-box { margin: 0 1px 0 4px !important; }

/* :::: URL Bar :::: */
#page-proxy-stack image { margin: 0 -2px 0 2px !important; }
#urlbar .progress-bar { opacity: .6 !important; }
#urlbar .autocomplete-textbox-container { -moz-border-radius: 12px 0 0 12px !important; }
#urlbar .autocomplete-history-dropmarker { 
  -moz-border-radius: 0 12px 12px 0 !important;
  background-color: transparent !important;
  border: none !important;
  margin-left: 1px !important;
}
#urlbar > .autocomplete-history-dropmarker[open="true"],
#urlbar > .autocomplete-history-dropmarker:hover,
#urlbar > .autocomplete-history-dropmarker:hover:active { background: transparent !important; }
[browsingmode=private] #urlbar { -moz-box-shadow: inset -5px -12px 18px -6px gray, inset 0 1px 2px rgba(0, 0, 0, 0.7) !important; }
#urlbar[level="high"]   { -moz-box-shadow: inset -5px -12px 18px -6px #B8F8B8, inset 0 1px 2px rgba(0, 0, 0, 0.7) !important; }
#urlbar[level="low"]    { -moz-box-shadow: inset -5px -12px 18px -6px #F5F6BE, inset 0 1px 2px rgba(0, 0, 0, 0.7) !important; }
#urlbar[level="broken"] { -moz-box-shadow: inset -5px -12px 18px -6px #C11313, inset 0 1px 2px rgba(0, 0, 0, 0.7) !important; }
#urlbar[readonly="true"] { 
  -moz-box-shadow: inset -5px -12px 18px -6px lightgrey, inset 0 1px 2px rgba(0, 0, 0, 0.7) !important;
  color: dimgray !important; 
}

/* :::: Identity Box :::: */
#identity-box, #identity-box hbox {
 background: transparent !important;
 -moz-border-radius: 12px 0 0 12px !important;
 -moz-box-shadow: none !important;
}
#identity-box,
#identity-box.verifiedIdentity,
#identity-box.verifiedDomain {
 border-top: 1px solid transparent !important;
 border-bottom: 1px solid transparent !important;
 border-left: none !important;
 margin-left: 0px !important;
}
#identity-icon-label {
 color: black !important;
 text-shadow: 0px 0px 3px white, 1px 1px 3px white, -1px -1px 3px white !important;
}
