css:
/* AGENT_SHEET */
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document
  url(http://www.google.com/),
  url-prefix(http://www.google.com/webhp),
  url-prefix(http://www.google.com/search?),
  url-prefix(http://www.google.com/#hl=),
  url-prefix(http://www.google.com/#q=),
  url-prefix(http://images.google.com/webhp),
  url(http://www.google.com/intl/en/),
  url-prefix(http://www.google.com/movies),
  url(http://www.google.co.uk/),
  url-prefix(http://www.google.co.uk/webhp),
  url-prefix(http://www.google.co.uk/search?),
  url-prefix(http://www.google.co.uk/#hl=),
  url-prefix(http://www.google.co.uk/#q=),
  url-prefix(http://images.google.co.uk/webhp),
  url-prefix(http://www.google.co.uk/movies),
  url(http://www.google.ca/),
  url-prefix(http://www.google.ca/webhp),
  url-prefix(http://www.google.ca/search?),
  url-prefix(http://www.google.ca/#hl=),
  url-prefix(http://www.google.ca/#q=),
  url-prefix(http://images.google.ca/webhp),
  url-prefix(http://www.google.ca/movies) {
body {
	background:url("http://i577.photobucket.com/albums/ss216/rover35si/bg.png") repeat !important;
	color: #fff !important;
}

/* disable dashed focus outline */
:focus { outline: none !important; }

/* small logo */
a#logo > img {
	width: 0 !important;
	height: 106px !important;
	padding-left: 164px !important;
}

/* Hide small logo text link, and intl text name */
a#logo, #lgpd + div > div[style^="color: rgb(102, 102, 102); font-size: 16px"] { font-size: 0 !important; }

/* Google bar "menu" and Google Suggest
---------------------------------------*/

b,p,a{ color: yellow !important; }

div[id=res]{
	-moz-column-count: auto;
	-moz-column-width: 500px !important;
	-moz-column-gap: 20px !important;
}
#gbi,#gbg{background:black !important;}
font{ display: none !important; }

/* Replacement images
---------------------*/
/* big logo */
img[src*="logos/"], img[src$="logo.gif"], div[style*="logo_plain.png"], img[src$="logo_google_suggest.gif"], img[src^="logos/holiday"], img[src^="/intl/"][alt="Google"][title="Google"] {
	margin-top: 30px !important;
	width: 0 !important;
	height: 123px !important;
	padding-left: 18% !important;
	background-image: url("http://i577.photobucket.com/albums/ss216/rover35si/JPG115-1.png") !important;
}

input {
	Font-Family: candara,verdana,tahoma !important;
	text-shadow: 1px 1px 1px grey !important;
}

input { margin-top: 5px !important; }

/* reset web form colors */
input *, textarea *, .aAutoComplete {
	background-color: -moz-Field !important;
	color: -moz-fieldtext !important;
}

input, textarea, select {
	-moz-appearance: none !important;
	color: #85D7A7 !important;
	padding: 5px !important;
	width: 265px;
	background: url("data:image/gif;base64,R0lGODlhAQATAJEAAC1EPh4xLSI1MSc8NyH5BAAAAAAALAAAAAABABMAAAIHTCQzwO1dAAA7") !important;
	border: 1px solid #000 !important;
}

textarea, input, select {
	background: #2d443e !important;
	opacity: 0.8 !important;
}

/* URL text */
.a, cite, .cite, .cite:link { color: red !important; }

/* "File Format" */
.f { color: #ffc !important; }

/* File type (ex: [PDF]) next to link */
.g .w { color: #ff6 !important; }

.gac_a > .gac_c { color: #fff !important; }

/* ad URL */
.gac_h { color: #d8d4ac !important; }

/* Google Suggest ads */
.gac_n,#flp,.std,.autoPagerS { display: none !important; }
.gac_m{background-color: black !important; }
.gbd { border-color: #4b5f78 !important; }

.gbh { border: none !important; }

/* dashes between Cached and Similar links */
.gl { color: #999 !important; }

/* hide borders */
.k { background: none !important; }

.lsb[name="btnG"]{
	height: 25px !important;
	width: 100px !important;
	font-size: 85% !important;
}

.lsb[name="btnI"]{
	height: 25px !important;
	width: 100px !important;
	font-size: 85% !important;
}

/* "more options" */
.tbo #tbd {
	background: transparent !important;
	border-color: #acc7e5 !important;
}

/* "nav" sprite sheet */
.w10, .w11, .w20, .w21, .w24, .wci, .wpb, .w4, .w5, .w40, .w41, .w44, .w50, .w51, .w54, li.w0 .w5, li.w1 .w5,
.csb, .ss,
.tbi div, #tbp,
a#logo > img {
	background-image: url("data:image/png;base64,") !important;
}
#sff{padding-left:50px !important;padding-top:0px !important;padding-bottom:0px !important;}
#bsf{ display: none !important; }
#ssb{margin-top:-10px !important;}
/* big bar after bottom pager */

#navbar + br + table td[bgcolor="#e5ecf9"] {
	background: #303d4c url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAgAAABkCAIAAAA0W+b9AAAABGdBTUEAAK/INwWK6QAAABl0RVh0U29mdHdhcmUAQWRvYmUgSW1hZ2VSZWFkeXHJZTwAAABnSURBVHjaYrRwi2DABpgYcAB6SLD8Z/iPXQKHODl2MOAwiyzn4rLj/3+ahxUuO3C6iop2kBMkVIta3EbhiQ+apxIWHEFF1QTHQIf88Z8OGYd6ziW9LBnQEm5UYlRiVGJUghoSAAEGAOYBHq+LOtzMAAAAAElFTkSuQmCC) repeat-x !important;
	border-top-color: #475a70 !important;
	border-bottom-color: #354353 !important;
}

#cnt{ max-width: 100% !important; }

/* fix for fade-in effect on front page */
#fctr, #fmsg, #ghead, #sbl, .promo, .fade { background: transparent !important; }

/* Ads
------*/
#mbEnd { border-left: 1px solid transparent !important; }

/* hide search result ads (delete next line if you want them) */
#mbEnd, #tads { display: none !important; }

/* hide old pager images */
#navbar td img { visibility: hidden !important; }

/* Search results
-----------------*/
/* big link */
#res .g .r a { color: yellow !important; }
#res .g .r a:hover{ color: green !important; }
#res .g .r a:active,#res .g .r a:focus,a:focus,a:active{ background:transparent !important; }
a:hover,p:hover,b:hover{color:red !important;}
/* link description */
#res .g .s { color: #fff !important; }

/* cached and similar links */
#res .g .s .gl a { color: red !important; }

/* visited cached and similar links */
#res .g .s .gl a:visited { opacity: 0.6 !important; }

/* "video results" time info */
#res .g .ts font[color="#666666"] { color: #ffc !important; }

/* big link visited */
#res .g a:visited { opacity: 0.5 !important; }

/* image thumbnails */
#res .g img { border: none !important; }

/* larger link title */
#res .l { font-size: 1.2em !important; }

/* more line spacing on descriptions */
#res .s { line-height: 1.4 !important; }

/* sub page links (on first link result) */
#res .s .slk a { color: #acc7e5 !important; opacity: 0.8 !important; }

/* visited sub page links  */
#res .s .slk a:visited { opacity: 0.5 !important; }

/* source code listing */
#res tt { color: #fff !important; }

#sbl,#fctr,#ghead{ background: transparent !important; }

/* Bars
-------*/
/* results bar */
#ssb,
form[action="/movies"] tr[bgcolor="#e5ecf9"],
#canvas > .heading {
	background: #2a3542 url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAcAAAAaCAIAAAD0eNHBAAAABGdBTUEAAK/INwWK6QAAABl0RVh0U29mdHdhcmUAQWRvYmUgSW1hZ2VSZWFkeXHJZTwAAABPSURBVHjatJCxDQAhDMSOF5MxASOwf4/ocgyAkULxKS3rIrm0PnTcJ7q/aJUN1EjDAXTOhQvkOiL97bLALu5K6OY7vDVjV/m+pF46bAEGAAT6LPtxqpMMAAAAAElFTkSuQmCC) repeat-x !important;
	border-top-color: #536982 !important;
}

/* Related searches bar */
#tbt5 {
	background: #303c4c url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAACJCAIAAABilMVrAAAABGdBTUEAAK/INwWK6QAAABl0RVh0U29mdHdhcmUAQWRvYmUgSW1hZ2VSZWFkeXHJZTwAAABcSURBVHjalJAxDsAwCAM5/pI5e///sEYqS4qdqgMiAgcO55hXRpBErLyCyjJob5S+arQ/Srv3cXoMx7ZLcVaPDw4xF8vBwZcXj+HG+uE8o83jxy2nu63nT/0WYACIxgHLMP/PmAAAAABJRU5ErkJggg==) repeat-x !important;
	}


}
