css:
@-moz-document domain(beta.crunchyroll.com) {
  .c-progress-bar__progress {
    background-color: #2195ed;
  }
  .c-carousel-tabs__tab-indicator:before {
    background-color: #2195ed;
    content: "";
    display: block;
    height: 100%;
    transform: scaleX(0);
    transform-origin: left center;
    width: 100%;
}
  .c-feed-divider--odd {
    background-image: linear-gradient(to var(--cr-start-direction),#2195ed,#2195ed);
}
  .c-feed-divider--even {
    background-image: linear-gradient(to var(--cr-start-direction),#2195ed,#2195ed);
}
  .c-button--type-one {
    --initial-shadow: none;
    --interaction-shadow: none;
    background-color: #2195ed;
    color: #141519;
}
  .c-button--type-one-weak {
    color: #2195ed;
}
  .c-button-type-one:hover {
    background-color: #1b78bf;
  }
  .c-crunchyroll-horizontal-beta-svg path {
    fill: #2195ed;
  }
  .account-item-premium-icon{
    fill: #2195ed;
  }
  .erc-user-menu-account-item .account-item-premium-label {
    word-wrap: break-word;
    color: #2195ed;
    overflow-wrap: break-word;
    word-break: break-word;
  }
  .erc-current-media-info .show-title-link {
    color: #2195ed;
    margin-bottom: 0.25rem;
    max-width: 100%;
}
  .c-username--premium {
    color: #2195ed;
}
  .css-1dbjc4n {
    background-color: #2195ed;
}
  .r-1pi2tsx {
    background-color: #2195ed;
  }
  }
