css:
@-moz-document url-prefix("https://music.youtube.com") {
/* Анимация открытия / закрытия проигрывателя */
ytmusic-app-layout > [slot=player-page] {
    transition: transform 1s cubic-bezier(0.3, 0, 0.3, 1);
}

/* Видимость страницы */
#content {
    visibility: visible !important;
}
#content ytmusic-browse-response {
    display: block !important;
}

/* Задний фон */
#player-page {
    backdrop-filter: blur(10px) brightness(0.5);
    background: #00000050;
}

/* Меню сверху */
#nav-bar-background {
	backdrop-filter: blur(10px) brightness(0.5);
    background: #00000050 !important;
}
#nav-bar-divider {
	display: none;
}

/* Панель плеера */
#player-bar-background {
    backdrop-filter: blur(10px) brightness(0.5);
    background: #00000050 !important;
}
ytmusic-player-bar {
    background: transparent;
}

/* Обложка (фото) */
yt-img-shadow[object-fit=COVER] img.yt-img-shadow {
	border-radius: 25px;
}
ytmusic-player {
	background: transparent;
}

/* Обложка (видео) */
.song-media-controls.ytmusic-player {
	border-radius: 25px;
}
.html5-video-player:not(.ytp-transparent) {
	border-radius: 25px;
	background: transparent;
}

/* Треки */
ytmusic-player-queue-item {
	transition: .1s linear;
	border-radius: 15px;
}

/* Ползунок прогресса песни */
#primaryProgress {
    transition: 1s linear;
}
#sliderKnob {
    transition: 1s linear;
}

/* Все кнопки */
tp-yt-paper-icon-button {
    transition: .2s linear;
}
}
