css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("twitter.com") {
   div.tab,#side{-moz-border-radius: 0.8em !important;}
   #footer,#navigation{-moz-border-radius: 0.4em !important;padding:10px !important}

   #side div.msg + ul{display:none}
   
   div.tab tr td+td {font-size:13px !important}

   tr.odd{ -moz-outline: 1px solid #dddeff !important;-moz-outline-radius: 0.4em !important;color:#777 !important}
   tr.odd:hover{ -moz-outline: 1px solid #555 !important;-moz-outline-radius: 0.4em !important;color:black !important}

   tr.even{background-color:#FFCBFC !important;}
   
   a:hover{text-decoration:none !important}

   td.thumb a img{border:1px black solid !important;-moz-border-radius:0.4em !important;padding:2px !important;background-color:#fff !important;}
   td.thumb a:hover img{border:1px blue solid !important}
   td.thumb+td strong > a{color:#333399 !important;font-weight:normal !important}

   span.meta{font-size:10px !important;color:#777 !important;display:block !important;margin-top:14px !important;text-align:right !important;}
   span.meta a{font-size:10px !important}

