#content #doingForm textarea {font-family:tahoma, arial; font-size: 9pt}
#friends a[rel=contact] img { margin:0 4px -8px 10!important; text-decoration:none!important; border:1px solid #999!important;}

#friends a[rel=contact]:hover{text-decoration:underline !important}

}
@-moz-document domain("twitter.com") {
body {font-family: tahoma}
.desc, table.doing, div#side ul li em#currently, textarea#status {direction:rtl; text-align: justify}
table.doing tr td, div#side, textarea#status {font: .9em/1.8em tahoma}
.desc p.meta, table.doing tr td span.meta {direction:ltr; text-align: justify; display: block; padding-top: 0px}
div#side ul li em#currently {font: bold 1.5em/1.5em "arial", Times, serif; display:block}
.desc {font: bold 1em/1.5em "Times New Roman", Times, serif; display:block}
}
