css:
@-moz-document domain('thedailywtf.com') {

body {
    background-color: #242424;
}
#wrapper .content-container#navigation,
#wrapper .content-container .content #article-feed .article,
#wrapper .content#home-page #sidebar .sidebar-section,
#wrapper .content#article-page,
#wrapper .content#home-page #sidebar {
    background-color: #ccc;
}

#wrapper .content#home-page #sidebar .sidebar-section label {
    background-color: #3c3c3c;
}
#wrapper .content-container .content #article-feed .article hr,
 hr {
    border-color: #ddd !important;
}

blockquote,
.hljs,
pre > code {
    background-color: #ddd;
}

#footer, #footer a {
    color: #eee !important;
}

#wrapper .content#article-page .article-container .author-box .credentials {
    color: #222;
}

}
