css:
@-moz-document url-prefix("https://www.horsereality.com")
{
  :root
  {
    --bckg: #ffffff;
    --text: #000000;
    --txt: #fbdde0;
    --txthover: #76a2a0;
    --lnav: #dfb4b5;
    --gold: #00ffaa;
    --tnav: #9d7478;
    --hover: #dfb4b5;
    --bbhov: #dfb4b5;
    --footer: #9d7478;
    --stblk: #d7d3e1;
    --brdr: #9d7478;
    --subhdr: #dfb4b5;
    --odd: #fbdde0;
    --even: #ffffff;
    --bar: #C57879;
    --enrg: #dfb4b5;
    --enrg1: #C57879;
    --butt: #9d7478;
    --hrtabsel: #9d7478;
    --hrtab: #dfb4b5;
    --hrhdr: #9d7478;
    --hrsel: #c6bdde;
  }

  .center
  {
    background-color: var(--bckg);
    opacity: 90%;
    margin: 0;
    padding-bottom: 25px;
    float: right;
    min-height: 650px;
  }

  body.background.south-america, body.background.africa, body.background.asia, body.background.australia, body.background.europe, body.background.north-america
  {
    background-image: url(https://www.horsereality.com/images/profilebg_kickstarter.jpg);
  }

  .leftnav
  {
    float: left;
    background-color: var(--lnav);
    border: 2px solid var(--lnav);
    border-radius: 0px 0px 20px 20px;
    width: 145px;
    z-index: 900;
    padding: 10px;
    font-family: Abadi;
  }

  .leftnav>ul>li
  {
    width: 140px;
    text-transform: uppercase;
    color: var(--txt);
  }

  .leftnav>ul>li>ul>li a:active, .leftnav>ul>li>ul>li a:link, .leftnav>ul>li>ul>li a:visited
  {
    font-size: 13px;
    font-family: Abadi;
    font-weight: normal;
    text-transform: capitalize;
    color: var(--txt);
    padding: 0;
  }

  .leftnav>ul>li>ul>li a:active:hover, .leftnav>ul>li>ul>li a:link:hover, .leftnav>ul>li>ul>li a:visited:hover
  {
    color: var(--txthover);
    text-decoration: none;
  }

  .subtitle
  {
    font-size: 16px;
    font-family: Abadi;
    font-weight: 900;
    color: var(--txt);
    margin-bottom: 5px;
  }

  .container
  {
    width: 1130px;
    background-color: var(--odd);
    margin-left: auto;
    margin-right: auto;
    padding-top: 50px;
  }

  .bluebar_container
  {
    width: 100%;
    background-color: var(--tnav);
    border-bottom: 2px solid var(--tnav);
    height: 80px;
    position: relative;
    z-index: 1000;
    font-family: Abadi;
  }

  .toplinks ul li a:hover
  {
    color: var(--txthover);
  }

  ul li a:active, ul li a:link, ul li a:visited
  {
    font-family: Abadi;
    color: var(--txt);
    font-weight: normal;
    font-size: 14px;
  }

  #bbmenu.user ul.slidedown li a:hover
  {
    color: var(--txthover);
  }

  #bbmenu.user ul.slidedown li ul.slidedownmenu
  {
    position: absolute;
    display: none;
    background-color: var(--tnav);
    border-left: 2px var(--tnav);
    border-right: 2px var(--tnav);
    border-bottom: 2px var(--tnav);
    border-radius: 0px 0px 20px 20px;
    z-index: 9999;
    margin-top: 50px;
    width: 130px;
    padding: 10px 15px;
    font-family: Abadi;
    transistion: .5s all ease-in-out;
  }

  #bbmenu.user ul.slidedown li:hover, #bbmenu.user ul.slidedown li:focus, #bbmenu.user ul.slidedown li:active
  {
    background-color: var(--bbhov);
  }

  .xpvalue
  {
    float: left;
    display: block;
    background-color: var(--subhdr);
    background: var(--bar)!important;
  }

  .statusbar
  {
    float: left;
    width: 125px;
    margin: 5px 0 10px;
    background-color: var(--odd);
    border: 2px solid var(--odd);
    height: 16px;
    -webkit-border-radius: 2px;
    -moz-border-radius: 2px;
    border-radius: 2px;
    color: var(--text);
    text-align: center;
    font-size: 12px;
    font-family: Abadi;
    line-height: 16px;
    font-weight: 400;
  }

  .gamestats
  {
    float: right;
    font-family: Abadi;
    font-weight: normal;
    font-size: 13px;
    text-align: right;
    line-height: 14px;
    color: var(--txt);
    margin: 9px 0;
  }

  .gamestats a:link, .gamestats a:visited, .gamestats a:active
  {
    color: var(--txt);
    text-decoration: none;
  }

  .footer
  {
    background-color: var(--footer);
    width: 100%;
    margin: 0 auto;
    padding: 10px 0;
    font-family: Abadi;
  }

  .footer::after
  {
    font-weight: normal;
    float: right;
    color: var(--txt);
    font-size: 18px;
    font-family: Abadi;
    opacity: 65%;
    content: "Custom Theme by xo_pahlyb";
  }

  .block_tabclick:hover, .block_tabclick.tabsel
  {
    background-color: var(--stblk);
    border-radius: 20px 20px 20px 20px;
    cursor: pointer;
  }

  #store_left
  {
    float: left;
    width: 150px;
    padding: 10px;
    margin-top: 5px;
    margin-right: 10px;
    color: var(--text);
    text-transform: capitalize;
    line-height: 18px;
    font-size: 13px;
    font-family: Abadi;
    font-weight: 500;
  }

  table
  {
    background-color: var(--brdr);
    border-bottom: 3px solid var(--brdr);
    border-right: 1px solid var(--brdr);
    border-left: 1px solid var(--brdr);
    width: 100%;
  }

  table thead tr.top
  {
    background-color: var(--brdr);
    font-weight: 600;
    color: var(--text);
    font-size: 16px;
    font-family: Abadi;
    line-height: 21px;
  }

  table thead tr.sub
  {
    background-color: var(--subhdr);
    font-weight: 600;
    color: var(--txt);
    font-size: 14px;
    font-family: Abadi;
    line-height: 18px;
  }

  table tr td a:active, table tr td a:link, table tr td a:visited
  {
    text-decoration: none;
    color: var(--text);
    font-family: Abadi;
    font-weight: 600;
  }

  p
  {
    font-family: Abadi;
    font-weight: 400;
    font-size: 13px;
    line-height: 18px;
    color: var(--text);
  }

  table tr.odd
  {
    background-color: var(--odd);
  }

  table tr.even
  {
    background-color: var(--even);
  }

  .energybar_half
  {
    float: left;
    width: 274px;
    background-color: var(--brdr);
    border: 2px solid var(--brdr);
    height: 20px;
    -webkit-border-radius: 2px;
    -moz-border-radius: 2px;
    border-radius: 2px;
    line-height: 20px;
    margin: 3px 0;
    width: 118px;
  }

  .energy_val_half
  {
    position: absolute;
    z-index: 990;
    width: 274px;
    color: var(--txt);
    text-align: center;
    font-size: 11px;
    font-family: Abadi;
    line-height: 20px;
    font-weight: 400;
    width: 118px;
  }

  .energy
  {
    float: left;
    display: block;
    background: -webkit-gradient(linear,0 0,0 100%,from(var(--enrg1)),to(var(--enrg)))!important;
  }

  button.yellow, input[type=submit].yellow
  {
    background-color: var(--butt);
    border: 2px solid var(--brdr);
    border-radius: 20px 20px 20px 20px;
  }

  button.yellow:hover, input[type=submit].yellow:hover
  {
    background-color: var(--hover);
    border-radius: 20px 20px 20px 20px;
  }

  button.dark, input[type=submit].dark
  {
    font-size: 12px;
    font-family: Abadi;
    line-height: 18px;
    background-color: var(--butt);
    color: var(--txt);
    border-radius: 20px 20px 20px 20px;
    border: 2px solid var(--brdr);
    padding: 3px 8px;
  }

  button.dark:hover, input[type=submit].dark:hover
  {
    background-color: var(--hover);
    border-radius: 20px 20px 20px 20px;
  }

  .tabclick.tabsel, .tabclick2.tabsel, .tabclick3.tabsel
  {
    text-decoration: none;
    cursor: pointer;
    background-color: var(--hrtabsel);
    font-weight: 500;
    font-family: Abadi;
    color: var(--txt);
  }

  .tabclick, .tabclick2, .tabclick3
  {
    float: left;
    margin-right: 1px;
    padding: 5px 10px;
    background-color: var(--hrtab);
    border: 2px solid var(--brdr);
    text-align: center;
    border-top-left-radius: 8px;
    border-top-right-radius: 8px;
    font-weight: 500;
    font-family: Abadi;
    color: var(--txt);
  }

  body
  {
    color: var(--text);
    height: 100%;
    position: relative;
    width: 100%;
    line-height: 1;
    font-family: Abadi;
    font-weight: 400;
    font-size: 12px;
    word-wrap: break-word;
  }

  .top
  {
    background-color: var(--hrhdr);
    color: var(--txt);
    font-family: Abadi;
    font-size: 16px;
    font-weight: 600;
    line-height: 21px;
    padding: 5px;
    width: calc(100% - 10px);
    float: left;
  }

  .training_left h6
  {
    font-size: 16px;
    font-family: Abadi;
    font-weight: 500;
    text-align: center;
    margin: 20px 10px;
    padding: 5px;
    color: var(--txt);
    background-color: var(--hrhdr);
    border: 2px solid var(--brdr);
  }

  .traincon.selected
  {
    background-color: var(--hrsel)!important;
    border: 2px solid var(--hrsel);
  }

  .training_left .horsetraining .traincon
  {
    display: flex;
    width: 110px;
    padding: 20px 10px 10px;
    margin-bottom: 10px;
    margin-left: 8px;
    margin-right: 8px;
    text-align: center;
    background-color: var(--odd);
    border: 2px solid var(--odd);
    border-radius: 20px 20px 20px 20px;
  }

  .trainbar>.exp
  {
    float: left;
    display: block;
    background-color: var(--brdr);
    background: var(--hrhdr)!important;
  }

  .training_right>.block
  {
    float: left;
    width: 278px;
    background-color: var(--even);
    padding: 10px;
  }

  .container_12 .grid_8.training_left
  {
    margin-top: 10px;
    background-color: var(--even);
    border-left: 2px solid var(--brdr);
    ;
    border-bottom: 2px solid var(--brdr);
    ;
    border-right: 2px solid var(--brdr);
    ;
    width: 610px;
    height: 100%;
  }

  .disc
  {
    float: left;
    display: block;
    background-color: var(--brdr);
    background: -webkit-gradient(linear,0 0,0 100%,from(var(--enrg1)),to(var(--enrg)))!important;
  }

  .affection
  {
    float: left;
    display: block;
    background-color: var(--brdr);
    background: -webkit-gradient(linear,0 0,0 100%,from(var(--enrg1)),to(var(--enrg)))!important;
  }

  .training_right>.block>.barcon>.trainbar>.trained
  {
    float: left;
    display: block;
    background-color: var(--brdr);
    background: -webkit-gradient(linear,0 0,0 100%,from(var(--enrg1)),to(var(--enrg)))!important;
  }

  .training_right>.block>.comp img, .training_right>.block>.show img
  {
    background-color: var(--odd);
    border: 2px solid var(--odd);
    border-radius: 20px 20px 20px 20px;
  }

  .horse_blockimg
  {
    float: left;
    width: 60px;
    padding: 5px;
    height: 60px;
    margin: 5px;
    background-color: var(--odd);
    border: 2px solid var(--odd);
    border-radius: 20px 20px 20px 20px;
  }

  .horse_left
  {
    position: absolute;
    top: 0;
    margin: 20px 0 20px 20px;
    width: 220px;
    padding: 10px;
    background-color: var(--odd);
    border: 2px solid var(--odd);
    border-radius: 20px 20px 20px 20px;
    opacity: 70%;
  }

  h1
  {
    font-size: 26px;
    line-height: 30px;
    font-family: Abadi;
    font-weight: 400;
    margin-bottom: 15px;
    color: var(--text);
  }

  p a:active, p a:link, p a:visited
  {
    color: var(--text);
    text-decoration: none;
    font-weight: 600;
    font-family: Abadi;
  }

  .horse_left .right a:active, .horse_left .right a:link, .horse_left .right a:visited
  {
    color: var(--text);
    text-decoration: none;
    font-weight: 700;
    font-family: Abadi;
  }

  h3
  {
    font-size: 16px;
    line-height: 18px;
    font-family: Abadi;
    font-weight: 700;
    color: var(--text);
  }

  .training_right>.block>.comp h3, .training_right>.block>.show h3
  {
    font-weight: 600;
    font-size: 14px;
    font-family: Abadi;
    line-height: 18px;
    margin-top: 5px;
    color: var(--text);
  }

  .energy_val
  {
    position: absolute;
    z-index: 990;
    width: 274px;
    color: var(--text);
    text-align: center;
    font-size: 11px;
    font-family: Abadi;
    line-height: 20px;
    font-weight: 400;
  }

  .genetic_table_row
  {
    float: left;
    width: 10%;
    border-bottom: 2px solid var(--odd);
  }

  .subtop
  {
    background-color: var(--subhdr);
    color: var(--gold);
    font-family: Abadi;
    font-size: 14px;
    font-weight: 600;
    line-height: 18px;
    color: var(--text);
    padding: 5px;
    float: left;
    width: 928px;
  }

  .grid_6.genetics
  {
    margin-top: 10px;
    background-color: var(--bckg);
    border-left: 2px solid var(--brdr);
    border-bottom: 2px solid var(--brdr);
    border-right: 2px solid var(--brdr);
    width: 454px;
    font-size: 13px;
    font-family: Abadi;
    line-height: 16px;
  }

  .genetic_result
  {
    float: right;
    width: 78px;
    padding: 10px;
    background-color: var(--odd);
    font-weight: 400;
    font-family: Abadi;
    text-align: center;
  }

  .genetic_tests .test_block .first
  {
    float: left;
    width: 20px;
    padding: 10px;
    background-color: var(--odd);
    text-align: center;
  }

  .genetic_potential
  {
    float: left;
    width: 100px;
    padding: 10px;
    font-weight: 600;
    font-family: Abadi;
    background-color: var(--odd);
    ;
  }

  .quote
  {
    position: relative;
    background-image: url(https://www.horsereality.com/images/horse_conformation.png);
    background-position: right bottom;
    background-repeat: no-repeat;
    background-size: contain;
    font-family: Abadi;
    font-weight: 400;
    font-size: 13px;
    line-height: 22px;
    color: var(--text);
    padding: 10px 15px;
  }

  .container_12 .grid_4.training_right
  {
    margin-top: 10px;
    background-color: var(--bckg);
    border-left: 2px solid var(--brdr);
    border-bottom: 2px solid var(--brdr);
    border-right: 2px solid var(--brdr);
    width: 298px;
  }

  .row_300
  {
    float: left;
    width: 288px;
    padding: 8px 5px;
    background-color: var(--bckg);
  }

  .odd
  {
    background-color: var(--odd);
  }

  .even
  {
    background-color: var(--even);
  }

  .container_12 .grid_12.stable_block
  {
    background-color: var(--bckg);
    border-left: 1px solid var(--brdr);
    border-bottom: 2px solid var(--brdr);
    border-right: 1px solid var(--brdr);
    width: 938px;
    margin: 10px;
  }

  .container_12 .grid_6.half_block
  {
    background-color: var(--bckg);
    border-left: 1px solid var(--brdr);
    border-bottom: 2px solid var(--brdr);
    border-right: 1px solid var(--brdr);
    width: 458px;
    margin: 10px;
  }

  .pedigree
  {
    float: left;
    background-color: var(--bckg);
  }

  .parents a:active p, .parents a:link p, .parents a:visited p
  {
    color: var(--text);
    font-weight: bold;
  }

  .grandparents a:active p, .grandparents a:link p, .grandparents a:visited p
  {
    color: var(--text);
    font-weight: bold;
  }

  .ggrandparents a:active p, .ggrandparents a:link p, .ggrandparents a:visited p
  {
    color: var(--text);
    font-weight: bold;
  }

  .horse_xp
  {
    float: left;
    display: block;
    background-color: var(--gold);
    background: -webkit-gradient(linear,0 0,0 100%,from(var(--enrg1)),to(var(--enrg)))!important;
  }

  .breadcrumbs
  {
    background-color: var(--hrhdr);
    -webkit-border-radius: 3px;
    -moz-border-radius: 3px;
    border-radius: 3px;
    margin: 15px 10px;
    padding: 8px 15px;
    float: left;
    width: calc(100% - 50px);
    font-family: Abadi;
    font-weight: 400;
    font-size: 12px;
    line-height: 14px;
    color: var(--txt);
  }

  .breadcrumbs a:link, .breadcrumbs a:visited, .breadcrumbs a:active
  {
    color: var(--txt);
    text-decoration: none;
  }

  .storeblocks, .horseblocks
  {
    display: flex;
    width: 220px;
    margin: 5px;
    padding: 10px 10px 10px 15px;
    background-color: var(--odd);
    border: var(--text);
    border-radius: 20px 20px 20px 20px;
    position: relative;
  }

  #store_left.tabnav .store_tabclick:hover, #store_left.tabnav .store_tabclick.tabsel
  {
    background-color: var(--hover);
    cursor: pointer;
  }

  .col_200 a:active, .col_200 a:link, .col_200 a:visited
  {
    color: var(--text);
    text-decoration: none;
    font-weight: 600;
    font-size: 13px;
    font-family: Abadi;
  }

  .col_150 a:active, .col_150 a:link, .col_150 a:visited
  {
    color: var(--text);
    text-decoration: none;
    font-weight: 600;
    font-size: 13px;
    font-family: Abadi;
  }

  .col_500 a:active, .col_500 a:link, .col_500 a:visited
  {
    color: var(--text);
    text-decoration: none;
    font-weight: 600;
    font-family: Abadi;
    font-size: 13px;
  }

  .col_300 a:active, .col_300 a:link, .col_300 a:visited
  {
    color: var(--text);
    text-decoration: none;
    font-weight: 600;
    font-size: 13px;
    font-family: Abadi;
  }

  #forum .row_960.no_padding
  {
    padding: 0;
    width: 938px;
    background-color: var(--bckg);
  }

  button.green, input[type=submit].green
  {
    background-color: var(--butt);
    padding: 5px 20px;
  }

  button.green:hover, input[type=submit].green:hover
  {
    background-color: var(--hover);
  }

  button.gray, input[type=submit].gray
  {
    background-color: var(--butt);
    color: var(--white);
    padding: 5px 20px;
  }

  button.gray:hover, input[type=submit].gray:hover
  {
    background-color: var(--hover);
  }

  .numselected
  {
    color: var(--text);
    font-weight: 700;
    font-family: Abadi;
    padding: 5px 10px;
    background: var(--subhdr);
    margin: 0 3px;
  }

  .npc_box
  {
    float: left;
    width: 940px;
    margin: 10px;
    background-color: var(--odd);
    border: var(--text);
    border-radius: 0px 20px 20px 0px;
  }

  .store_title
  {
    font-size: 14px;
    font-family: Abadi;
    text-align: center;
    font-weight: 700!important;
    color: var(--txt);
    margin-bottom: 15px;
  }

  .grid_12 form fieldset label, .grid_12 fieldset label forum topic submit, search text
  {
    float: left;
    width: 200px;
    margin-right: 10px;
    color: var(--text);
    font-size: 16px;
    font-family: Abadi;
    font-weight: 500;
  }

  .premium-only
  {
    border: 1px var(--brdr) solid;
    border-radius: 20px 20px 20px 20px;
    background: var(--odd);
    opacity: 75%;
    padding: 8px;
  }

  .column_160 a:hover, .column_200 a:hover, .column_300 a:hover
  {
    color: var(--text);
  }

  .column_200 a:active, .column_200 a:link, .column_200 a:visited
  {
    color: var(--text);
    text-decoration: none;
  }

  .numbers
  {
    background: var(--hover);
    padding: 5px 10px;
    margin: 0 3px;
    text-align: center;
  }

  .pagenumbers a:active, .pagenumbers a:link, .pagenumbers a:visited
  {
    color: var(--text);
    text-decoration: none;
  }

  table tr
  {
    background-color: var(--odd);
  }

  .exchange_actions_window
  {
    display: inline-block;
    width: 45%;
    border: 1px var(--brdr) solid;
    padding: 7px;
  }

  .exchange_actions_window table
  {
    border: none;
    width: 75%;
    margin: 15px auto;
  }

  .exchange_actions_window_buy h2
  {
    border-bottom-color: var(--brdr);
  }

  h2
  {
    font-size: 18px;
    line-height: 21px;
    font-family: Abadi;
    font-weight: 400;
    color: var(--text);
    margin: 5px 0;
  }

  .exchange_actions_window_sell h2
  {
